import React, { useState, useEffect } from 'react';
import { Box, TextField, MenuItem, Select, InputLabel, FormControl, Button, Grid, Typography } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const EditarUsuario = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const navigate = useNavigate();
  const { userId } = useParams(); // Obtener el ID del usuario de la URL
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rol, setRol] = useState('');
  const [roles, setRoles] = useState([]);

  // Verificar si el usuario está autenticado
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    } else {
      fetchRoles(); // Si está autenticado, carga los roles
      fetchUsuario(userId); // Obtiene los datos del usuario a editar
    }
  }, [navigate, userId]);

  // Función para obtener los roles desde la API
  const fetchRoles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/roles/rol');
      setRoles(response.data);
    } catch (error) {
      console.error('Error al obtener los roles:', error);
    }
  };

  // Función para obtener los datos del usuario a editar
  const fetchUsuario = async (id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/auth/users/${id}`);
      const usuario = response.data;
      
      // Pre-cargar los datos del formulario con los datos del usuario
      setNombre(usuario.u_nombre);
      setApellido(usuario.u_apellido);
      setUsername(usuario.u_usuario);
      setRol(usuario.u_rol);  // Asumiendo que el rol está en el objeto del usuario
    } catch (error) {
      console.error('Error al obtener los datos del usuario:', error);
      setShowAlert({ severity: 'error', message: 'Hubo un error al obtener los datos del usuario.' });
    }
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (event) => {
    event.preventDefault();

    // Validar campos requeridos
    if (!nombre || !apellido || !username || !password || !rol) {
      setShowAlert({ severity: 'warning', message: 'Por favor complete todos los campos' });
      return;
    }

    const usuarioActualizado = {
      nombre,
      apellido,
      username,
      password,
      rol,
    };

    try {
      // Realiza la solicitud PUT para actualizar el usuario
      const response = await axios.put(`http://localhost:5000/api/auth/users/update/${userId}`, usuarioActualizado);
      
      // Aquí asumimos que la API devuelve el mensaje en "msg"
      if (response.data.msg) {
        setShowAlert({ severity: 'error', message: response.data.msg }); // Mostrar el mensaje de error
      } else {
        setShowAlert({ severity: 'success', message: 'Usuario actualizado exitosamente' });
        
        // Redirige después de 3 segundos
        setTimeout(() => navigate('/Seguridad/Usuarios'), 3000);
      }
    } catch (error) {
      // Mostrar el error completo
      console.error('Error al actualizar el usuario:', error.response || error); // Mostrar detalles del error

      // Si la respuesta de error tiene un mensaje específico
      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({ severity: 'error', message: error.response.data.msg }); // Mostrar el mensaje específico de la API
      } else {
        setShowAlert({ severity: 'error', message: 'Hubo un error al actualizar el usuario. Inténtalo de nuevo.' });
      }
    }
  };

  return (
    <Box sx={{ width: '100%', maxWidth: 600, margin: 'auto', mt: 4 }}>
      
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={1000} // La alerta se cierra después de 5 segundos
          sx={{ mb: 4 }} // Agregar margen inferior a la alerta
        />
      )}

      <Typography variant="h5" sx={{ textAlign: 'center', mb: 4 }}>Edita el usuario</Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Campo Nombre */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Nombre"
              variant="outlined"
              fullWidth
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
          </Grid>

          {/* Campo Apellido */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Apellido"
              variant="outlined"
              fullWidth
              value={apellido}
              onChange={(e) => setApellido(e.target.value)}
              required
            />
          </Grid>

          {/* Campo Username */}
          <Grid item xs={12}>
            <TextField
              label="Username"
              variant="outlined"
              fullWidth
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </Grid>

          {/* Campo Password */}
          <Grid item xs={12}>
            <TextField
              label="Password"
              type="password"
              variant="outlined"
              fullWidth
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </Grid>

          {/* Campo Rol (con select) */}
          <Grid item xs={12}>
            <FormControl fullWidth required>
              <InputLabel>Rol</InputLabel>
              <Select
                value={rol}
                onChange={(e) => setRol(e.target.value)}
                label="Rol"
                required
              >
                {/* Mapea los roles obtenidos desde la API */}
                {roles.map((role) => (
                  <MenuItem key={role.idRol} value={role.idRol}>
                    {role.descripcion}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          {/* Botón para actualizar el usuario */}
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Actualizar Usuario
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default EditarUsuario;
