import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend
} from 'chart.js';
import { jwtDecode } from 'jwt-decode';

// Registrar las partes necesarias de Chart.js
ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardCharts = () => {
    const [proyectos, setProyectos] = useState([]);
    const [loading, setLoading] = useState(true);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        const decoded = jwtDecode(token);
        const id = decoded.user.id;

        try {
            // Hacer una solicitud GET a la API para obtener los proyectos
            const response = await axios.get(`http://localhost:5000/api/grafica/VencimientoProyecto/${id}`);
            const { labels, datasets, vencidos, noVencidos, vencidosNombres, noVencidosNombres } = response.data;

            // Guardar los datos para las gráficas
            setProyectos({
                labels,
                datasets,
                vencidos,
                noVencidos,
                vencidosNombres,
                noVencidosNombres
            });
            setLoading(false);
        } catch (error) {
            console.error('Error al obtener los proyectos:', error);
            setLoading(false);
        }
    }, [isAuthenticated, token]);

    useEffect(() => {
        if (isAuthenticated) {
            fetchProyectos();
        }
    }, [isAuthenticated, fetchProyectos]);

    if (loading) return <div>Loading...</div>;

    return (
        <div>
            <h2>Proyectos Próximos a Vencer</h2>
            <Pie
                data={{
                    labels: proyectos.labels,
                    datasets: proyectos.datasets
                }}
                options={{
                    responsive: true,
                    plugins: {
                        legend: {
                            position: 'top',
                        },
                        tooltip: {
                            callbacks: {
                                title: (tooltipItems) => tooltipItems[0].label,
                                label: (tooltipItem) => {
                                    const project = tooltipItem.raw === 1 ? proyectos.vencidosNombres : proyectos.noVencidosNombres;
                                    return `${tooltipItem.raw} Proyectos: ${project.join(", ")}`;
                                }
                            }
                        }
                    }
                }}
            />
        </div>
    );
};

export default DashboardCharts;
