import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend
} from 'chart.js';
import { jwtDecode } from 'jwt-decode';

ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardCharts = () => {
    const [proyectos, setProyectos] = useState({
        labels: [],
        datasets: [],
        vencidos: 0,
        noVencidos: 0,
        vencidosNombres: [],
        noVencidosNombres: []
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [startDate, setStartDate] = useState('');  // Estado para la fecha de inicio
    const [endDate, setEndDate] = useState('');      // Estado para la fecha de fin
    const [noData, setNoData] = useState(false);     // Estado para manejar la falta de datos

    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        try {
            const decoded = jwtDecode(token);
            const id = decoded.user?.id; // Verifica que `user` exista en el token.

            const config = {
                headers: {
                    'x-auth-token': token, 
                },
                params: {} // Añadimos parámetros de fecha para la solicitud
            };

            // Solo añadimos fechas a la solicitud si están definidas
            if (startDate) config.params.startDate = startDate;
            if (endDate) config.params.endDate = endDate;

            const response = await axios.get(
                `http://localhost:5000/api/grafica/VencimientoProyecto/${id}`,
                config
            );

            const { labels, vencidos, noVencidos, vencidosNombres, noVencidosNombres } = response.data;

            if (!labels || !Array.isArray(labels) || !vencidos || !noVencidos || !vencidosNombres || !noVencidosNombres) {
                setError('Datos incompletos recibidos de la API.');
                return;
            }

            setProyectos({
                labels,
                datasets: [
                    {
                        data: [vencidos, noVencidos],
                        backgroundColor: ['#FF6384', '#36A2EB'],
                    },
                ],
                vencidos,
                noVencidos,
                vencidosNombres,
                noVencidosNombres,
            });
        } catch (err) {
            console.error('Error al obtener los proyectos:', err);
            const errorMsg = err.response?.data?.msg || 'Error desconocido';
            setError(`No se pudieron cargar los datos de los proyectos: ${errorMsg}`);
        } finally {
            setLoading(false);
        }
    }, [isAuthenticated, token, startDate, endDate]);

    useEffect(() => {
        if (!isAuthenticated) return;
        fetchProyectos(); // Llama automáticamente a la función al cambiar las fechas
    }, [isAuthenticated, fetchProyectos]);
    

    const handleDateChange = () => {
        setLoading(true); // Inicia la carga cuando se cambian las fechas
        fetchProyectos(); // Vuelve a hacer la solicitud con las nuevas fechas
    };

    const handleClearDates = () => {
        setStartDate('');
        setEndDate('');
        setLoading(true);
        fetchProyectos(); // Recargar los datos sin filtros de fecha
    };

    if (loading) return <div>Cargando datos...</div>;

    if (error) {
        return <div>{error}</div>;
    }

    if (!proyectos.labels.length || !proyectos.datasets.length) {
        return <div>No se encontraron proyectos asignados.</div>;
    }

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Proyectos</h2>
            <div style={{ marginBottom: '20px', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '10px' }}>
                <div>
                    <input
                        type="date"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                        placeholder="Fecha de inicio"
                        style={{ padding: '5px', width: '150px' }}
                    />
                </div>
                <div>
                    <input
                        type="date"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                        placeholder="Fecha de fin"
                        style={{ padding: '5px', width: '150px' }}
                    />
                </div>
                <div>
                    <button
                        onClick={handleClearDates}
                        style={{
                            padding: '6px 12px',
                            backgroundColor: '#f44336',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            cursor: 'pointer'
                        }}
                    >
                        Limpiar
                    </button>
                </div>
            </div>

            {noData ? (
                <div style={{ textAlign: 'center', fontSize: '18px', color: '#888' }}>
                    <p>No se encontraron proyectos para este rango de fechas. Intenta con otro período.</p>
                </div>
            ) : null}

            {error && (
                <div style={{ color: 'red', textAlign: 'center' }}>
                    <p>{error}</p>
                </div>
            )}
            <Pie
                data={{
                    labels: proyectos.labels,
                    datasets: proyectos.datasets,
                }}
                options={{
                    responsive: true,
                    plugins: {
                        legend: {
                            position: 'top',
                        },
                        tooltip: {
                            callbacks: {
                                label: (tooltipItem) => {
                                    const index = tooltipItem.dataIndex;
                                    const projectNames = index === 0
                                        ? proyectos.vencidosNombres
                                        : proyectos.noVencidosNombres;
                                    return `${tooltipItem.raw} Proyectos: ${projectNames.join(', ')}`;
                                },
                            },
                        },
                    },
                }}
            />
        </div>
    );
};

export default DashboardCharts;
