import React, { useState, useEffect } from 'react';
import { Box, Grid, Typography, Checkbox, FormControlLabel, TextField } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom'; // Para obtener el id del rol
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Función para verificar autenticación

const CrearPermisos = () => {
  const [showAlert, setShowAlert] = useState(null); // Alerta
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el id del rol desde la URL

  const [rolName, setRolName] = useState('');
  const [descripcion, setDescripcion] = useState('');
  
  const [menus, setMenus] = useState([]);
  const [permissions, setPermissions] = useState({});

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    } else {
      if (id) {
        fetchRoleData(id);
      }
      fetchMenus();
    }
  }, [id, navigate]);

  const fetchRoleData = async (roleId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({ severity: 'error', message: 'No se encontró el token de autenticación.' });
        return;
      }
      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get(`http://localhost:5000/api/roles/roledit/${roleId}`, config);
      const roleData = response.data;
      setRolName(roleData.NOMBRE);
      setDescripcion(roleData.DESCRIPCION);
    } catch (error) {
      console.error('Error al cargar el rol:', error.response || error);
      setShowAlert({ severity: 'error', message: 'Hubo un error al cargar los datos del rol.' });
    }
  };

  const fetchMenus = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({ severity: 'error', message: 'No se encontró el token de autenticación.' });
        return;
      }
      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get('http://localhost:5000/api/navigation/menu', config);
      setMenus(response.data);
    } catch (error) {
      console.error('Error al cargar los menús:', error.response || error);
      setShowAlert({ severity: 'error', message: 'Hubo un error al cargar los menús.' });
    }
  };

  const handlePermissionChange = (menuId, action) => {
    setPermissions(prevState => {
      const updatedPermissions = { ...prevState };
      if (!updatedPermissions[menuId]) {
        updatedPermissions[menuId] = { view: false, edit: false, delete: false };
      }
      updatedPermissions[menuId][action] = !updatedPermissions[menuId][action];
      return updatedPermissions;
    });
  };

  // Organizar los menús y submenús jerárquicamente
  const organizeMenus = () => {
    const mainMenus = menus.filter(menu => menu.ParentMenuID === 0);
    const subMenus = menus.filter(menu => menu.ParentMenuID !== 0 && menu.ParentMenuID !== menu.MenuID);
    const subSubMenus = menus.filter(menu => menu.Accion === 1 || menu.Accion === 2);

    return mainMenus.map(mainMenu => ({
      ...mainMenu,
      subMenus: subMenus
        .filter(subMenu => subMenu.ParentMenuID === mainMenu.MenuID)
        .map(subMenu => ({
          ...subMenu,
          subSubMenus: subSubMenus.filter(subSubMenu => subSubMenu.ParentMenuID === subMenu.MenuID),
        })),
    }));
  };

  const organizedMenus = organizeMenus();

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" textAlign="center" gutterBottom>
        Asignación de permisos
      </Typography>

      <div style={{ marginBottom: '20px', marginTop: '20px' }}>
        <Typography variant="h6" gutterBottom>
          Datos del Rol:
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre del Rol"
              value={rolName}
              InputProps={{
                readOnly: true,
                style: { backgroundColor: '#f5f5f5' },
              }}
              variant="outlined"
              size="small"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              InputProps={{
                readOnly: true,
                style: { backgroundColor: '#f5f5f5' },
              }}
              variant="outlined"
              size="small"
            />
          </Grid>
        </Grid>
      </div>

      <Typography variant="h5" gutterBottom>
        Menús Principales
      </Typography>
      <Grid container spacing={2}>
        {organizedMenus.map((menu) => (
          <Grid item xs={12} sm={6} key={menu.MenuID}>
            <Box sx={{ border: '1px solid #ccc', padding: 2, borderRadius: 1 }}>
              <Typography variant="h6">{menu.Nombre}</Typography>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={permissions[menu.MenuID]?.view || false}
                    onChange={() => handlePermissionChange(menu.MenuID, 'view')}
                    color="primary"
                  />
                }
                label="Mostrar"
              />

              {/* Renderizar submenús y sub-submenús */}
              {menu.subMenus.map((subMenu) => (
                <Box key={subMenu.MenuID} sx={{ marginLeft: 3 }}>
                  <Typography variant="subtitle1">{subMenu.Nombre}</Typography>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={permissions[subMenu.MenuID]?.view || false}
                        onChange={() => handlePermissionChange(subMenu.MenuID, 'view')}
                        color="primary"
                      />
                    }
                    label="Mostrar"
                  />

                  {/* Sub-submenús */}
                  {subMenu.subSubMenus.map((subSubMenu) => (
                    <Box key={subSubMenu.MenuID} sx={{ marginLeft: 3 }}>
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={permissions[subSubMenu.MenuID]?.view || false}
                            onChange={() => handlePermissionChange(subSubMenu.MenuID, 'view')}
                            color="primary"
                          />
                        }
                        label={`Acción: ${subSubMenu.Nombre}`}
                      />
                    </Box>
                  ))}
                </Box>
              ))}
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default CrearPermisos;
