import React, { useState, useEffect, useCallback } from 'react';
import {
    Box,
    TextField,
    Button,
    Grid,
    Typography,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    MenuItem,
    Select,
    InputLabel,
    FormControl,
    CircularProgress,
} from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const CrearHito = () => {
    const { proyectoId } = useParams(); // Extraer proyectoId de la URL
    const [showAlert, setShowAlert] = useState(null);
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFin, setFechaFin] = useState('');
    const [estado, setEstado] = useState('Pendiente');
    const [proyecto, setProyecto] = useState(null);
    const [hitos, setHitos] = useState([]);
    const navigate = useNavigate();

    // Función general para realizar peticiones
    const makeRequest = async (method, url, data = {}) => {
        const token = localStorage.getItem('token');
        if (!token) {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el token de autenticación.',
            });
            return null;
        }

        const config = { headers: { 'x-auth-token': token } };
        try {
            const response = await axios[method](url, data, config);
            return response.data;
        } catch (error) {
            setShowAlert({
                severity: 'error',
                message: error.response?.data?.msg || 'Hubo un error al realizar la solicitud.',
            });
            return null;
        }
    };

    // Obtener proyecto y hitos
    const fetchProyecto = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/proyectos/${proyectoId}`);
        if (data) setProyecto(data);
    }, [proyectoId]);

    const fetchHitos = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        setHitos(data || []);
    }, [proyectoId]);

    // Cargar datos iniciales
    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login');
            return;
        }
        fetchProyecto();
        fetchHitos();
    }, [navigate, fetchProyecto, fetchHitos]);

    // Manejo del formulario
    const handleSubmit = async (e) => {
        e.preventDefault();
    
        if (!titulo || !descripcion || !fechaInicio || !fechaFin) {
            setShowAlert({
                severity: 'error',
                message: 'Todos los campos son obligatorios.',
            });
            return;
        }
    
        const newHito = {
            titulo,
            descripcion,
            fechaInicio,
            fechaFin,
            estado,
            proyectoId, // Relación con el proyecto
        };
    
        const response = await makeRequest('post', 'http://localhost:5000/api/hitos/register', newHito);
    
        if (response) {
            setShowAlert({
                severity: response.msg ? 'success' : 'error', // Cambiar según la presencia del mensaje
                message: response.msg || 'Error al crear el hito.',
            });
    
            if (response.msg) {
                // Limpiar formulario y actualizar hitos
                setTitulo('');
                setDescripcion('');
                setFechaInicio('');
                setFechaFin('');
                setEstado('Pendiente');
                fetchHitos();
            }
    
            setTimeout(() => setShowAlert(null), 2000);
        }
    };
    

    return (
        <Box sx={{ padding: 5 }}>
            {showAlert && (
                <AlertMessage
                    severity={showAlert.severity}
                    message={showAlert.message}
                    autoHideDuration={2000}
                />
            )}

            <Typography variant="h5" gutterBottom>
                {proyecto ? `Proyecto: ${proyecto.nombre}` : <CircularProgress size={24} />}
            </Typography>

            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Título del Hito"
                            value={titulo}
                            onChange={(e) => setTitulo(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Descripción"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de inicio"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaInicio}
                            onChange={(e) => setFechaInicio(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de finalización"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaFin}
                            onChange={(e) => setFechaFin(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel>Estado</InputLabel>
                            <Select
                                value={estado}
                                onChange={(e) => setEstado(e.target.value)}
                                label="Estado"
                            >
                                <MenuItem value="Pendiente">Pendiente</MenuItem>
                                <MenuItem value="En Proceso">En Proceso</MenuItem>
                                <MenuItem value="Finalizado">Finalizado</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            Crear Hito
                        </Button>
                    </Grid>
                </Grid>
            </form>

            <Paper sx={{ mt: 4, p: 3 }}>
                <Typography variant="h6" gutterBottom>Hitos del Proyecto</Typography>
                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Título</TableCell>
                                <TableCell>Descripción</TableCell>
                                <TableCell>Fecha Inicio</TableCell>
                                <TableCell>Fecha Fin</TableCell>
                                <TableCell>Estado</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {hitos.length > 0 ? (
                                hitos.map((hito) => (
                                    <TableRow key={hito._id}>
                                        <TableCell>{hito.titulo}</TableCell>
                                        <TableCell>{hito.descripcion}</TableCell>
                                        <TableCell>{hito.fechaInicio}</TableCell>
                                        <TableCell>{hito.fechaFin}</TableCell>
                                        <TableCell>{hito.estado}</TableCell>
                                    </TableRow>
                                ))
                            ) : (
                                <TableRow>
                                    <TableCell colSpan={5}>No hay hitos registrados.</TableCell>
                                </TableRow>
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </Box>
    );
};

export default CrearHito;
