import React, { useState, useEffect, useCallback } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel, FormControl } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [nombreProyecto, setNombreProyecto] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [hitos, setHitos] = useState([]); // Inicializar como un arreglo vacío
  const [resultadoEsperado, setResultadoEsperado] = useState('');
  const [pruebaRealizada, setPruebaRealizada] = useState('');
  const [estadoPrueba, setEstadoPrueba] = useState('');
  const [showPlanDePrueba, setShowPlanDePrueba] = useState(false); // Estado para mostrar los campos de plan de prueba
  const [hitoSeleccionado, setHitoSeleccionado] = useState(''); // Estado para el hito seleccionado
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el ID del proyecto desde la URL

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    }
  }, [navigate]);

  // Obtener los datos del proyecto
  useEffect(() => {
    const fetchProyecto = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/proyectos/proyecto/${id}`);
        const proyecto = response.data;

        // Asegúrate de convertir las fechas al formato correcto: YYYY-MM-DD
        setNombreProyecto(proyecto.nombre);
        setDescripcion(proyecto.descripcion);
      } catch (error) {
        console.error('Error al obtener el proyecto:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los datos del proyecto.',
        });
      }
    };

    if (id) {
      fetchProyecto();
    }
  }, [id]);

  // Función para recuperar los hitos usando useCallback
  const fetchHitos = useCallback(async () => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/hitos/hito/${id}`); // Recupera los hitos del proyecto
      setHitos(response.data || []);
    } catch (error) {
      console.error('Error al obtener los hitos:', error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los hitos.',
      });
    } finally {
      setLoading(false);
    }
  }, [id]);

  // Llamamos a fetchHitos solo cuando el componente se monta o cuando el id cambie
  useEffect(() => {
    fetchHitos();
  }, [fetchHitos]);

  // Función para manejar el envío del formulario (en este caso, solo validación)
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples para el plan de prueba
    if (!resultadoEsperado || !pruebaRealizada || !estadoPrueba || !hitoSeleccionado) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos del plan de prueba son obligatorios.',
      });
      return;
    }

    const planDePrueba = {
      resultadoEsperado,
      pruebaRealizada,
      estadoPrueba,
      hito: hitoSeleccionado,
    };

    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      // Aquí puedes enviar el plan de prueba a la API
      const response = await axios.post(`http://localhost:5000/api/proyectos/crearPlanDePrueba/${id}`, planDePrueba, config);

      if (response.data.msg === 'Plan de prueba creado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg,
        });

        // Redirigir después de 2 segundos
        setTimeout(() => {
          navigate('/proyectos/tareas');
        }, 2000);
      }
    } catch (error) {
      console.error('Error al crear el plan de prueba:', error.response || error);

      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el plan de prueba. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom align="center">
        Crear Plan de Prueba
      </Typography>

      {/* Datos del Proyecto */}
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Nombre del Proyecto"
            value={nombreProyecto}
            disabled
            variant="outlined"
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Descripción"
            value={descripcion}
            disabled
            variant="outlined"
          />
        </Grid>
      </Grid>

      {/* Botón para mostrar campos de Plan de Prueba */}
      <Grid item xs={12} mt={2}>
        <Button 
          variant="contained" 
          color="primary" 
          fullWidth 
          onClick={() => setShowPlanDePrueba(true)}
        >
          Crear Plan de Prueba
        </Button>
      </Grid>

      {/* Mostrar los campos del Plan de Prueba solo si se hizo clic en Crear Plan de Prueba */}
      {showPlanDePrueba && (
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2} mt={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Resultado Esperado"
                value={resultadoEsperado}
                onChange={(e) => setResultadoEsperado(e.target.value)}
                variant="outlined"
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Prueba Realizada"
                value={pruebaRealizada}
                onChange={(e) => setPruebaRealizada(e.target.value)}
                variant="outlined"
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required variant="outlined">
                <InputLabel>Estado de la Prueba</InputLabel>
                <Select
                  value={estadoPrueba}
                  onChange={(e) => setEstadoPrueba(e.target.value)}
                  label="Estado de la Prueba"
                >
                  <MenuItem value="Pendiente">Pendiente</MenuItem>
                  <MenuItem value="Aprobada">Aprobada</MenuItem>
                  <MenuItem value="Rechazada">Rechazada</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required variant="outlined">
                <InputLabel>Seleccionar Hito</InputLabel>
                <Select
                  value={hitoSeleccionado}
                  onChange={(e) => setHitoSeleccionado(e.target.value)}
                  label="Seleccionar Hito"
                >
                  {hitos.length > 0 ? (
                    hitos.map((hito, index) => (
                      <MenuItem key={index} value={hito.nombre}>
                        {hito.nombre}
                      </MenuItem>
                    ))
                  ) : (
                    <MenuItem value="" disabled>
                      No hay hitos disponibles
                    </MenuItem>
                  )}
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12} mt={2}>
              <Button
                variant="contained"
                color="primary"
                type="submit"
                fullWidth
              >
                Guardar Plan de Prueba
              </Button>
            </Grid>
          </Grid>
        </form>
      )}
    </Box>
  );
};

export default CrearPlanDePrueba;
