import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

// Importa tu logo
import Logo from '../assets/LogoPrinci.png'; // Ajusta la ruta según donde esté tu logo

const demoTheme = createTheme({
  palette: {
    mode: 'light',
  },
});

function DemoPageContent({ pathname }) {
  return (
    <Box
      sx={{
        py: 4,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <Typography>Dashboard content for {pathname}</Typography>
    </Box>
  );
}

export default function DashboardLayoutNavigationLinks() {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;
  const [menuItems, setMenuItems] = React.useState([]);
  const [open, setOpen] = React.useState(false);

  const fetchMenus = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    const decoded = jwtDecode(token);
    const username = decoded.user.username;

    try {
      const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
      setMenuItems(response.data);
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  React.useEffect(() => {
    fetchMenus();
  }, []);

  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen);
  };

  const navigation = menuItems.map(item => {
    if (item.ParentMenuID === 0) {
      return {
        segment: item.DisplayName,
        title: item.DisplayName,
        icon: <DescriptionIcon />,
        children: menuItems.filter(subItem => subItem.ParentMenuID === item.MenuID),
      };
    }
    return null;
  }).filter(Boolean);

  return (
    <AppProvider
      navigation={navigation.map(item => ({
        segment: item.segment,
        title: item.title,
        icon: item.icon,
        children: item.children.length > 0 ? item.children.map(subItem => ({
          segment: subItem.DisplayName,
          title: subItem.DisplayName,
          icon: <DescriptionIcon />,
          onClick: () => navigate(subItem.MenuURL),
        })) : [],
      }))}
      router={{
        pathname,
        navigate: (path) => navigate(path),
      }}
      theme={demoTheme}
    >
      <DashboardLayout>
        {/* Aquí puedes agregar tu logo en lugar del texto "Toolpad" */}
        <Box component="header" sx={{ display: 'flex', alignItems: 'center', padding: '16px' }}>
          <img src={Logo} alt="Logo" style={{ height: '50px' }} /> {/* Ajusta la altura según sea necesario */}
          <Typography variant="h6" sx={{ marginLeft: '8px' }}>
            Tu Aplicación
          </Typography>
        </Box>
        <DemoPageContent pathname={pathname} />
      </DashboardLayout>
    </AppProvider>
  );
}
