import React, { useState, useEffect } from 'react';
import {
    Button, TextField, Table, TableBody, TableCell, TableContainer,
    TableHead, TableRow, TablePagination, Paper, Box, Grid
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

function Usuario() {
    const [usuarios, setUsuarios] = useState([]);
    const [search, setSearch] = useState('');  // Estado para almacenar el valor del campo de búsqueda
    const [page, setPage] = useState(0);  // Página actual
    const [rowsPerPage, setRowsPerPage] = useState(5);  // Filas por página
    const navigate = useNavigate();

    // Obtener los usuarios desde la API
    const fetchUsuarios = async (searchTerm = '') => {
        const response = await fetch(`http://localhost:5000/api/auth/users?search=${searchTerm}`);
        const data = await response.json();
        setUsuarios(data);
    };

    useEffect(() => {
        fetchUsuarios(search);  // Al cargar, hacer la consulta con el término de búsqueda actual
    }, [search]);  // Rehacer la consulta siempre que el search cambie

    const handlePageChange = (event, newPage) => {
        setPage(newPage);
    };

    const handleRowsPerPageChange = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);  // Reiniciar la página cuando cambie el número de filas por página
    };

    // Redirigir al formulario de creación de usuario
    const handleCrearUsuario = () => {
        navigate('/crear-usuario');  // Redirige al formulario de creación de usuario
    };

    // Personalización de la paginación
    const CustomPaginationActions = (props) => {
        const { count, page, rowsPerPage, onPageChange } = props;

        const handleFirstPageButtonClick = (event) => {
            onPageChange(event, 0);
        };

        const handleBackButtonClick = (event) => {
            onPageChange(event, page - 1);
        };

        const handleNextButtonClick = (event) => {
            onPageChange(event, page + 1);
        };

        const handleLastPageButtonClick = (event) => {
            onPageChange(event, Math.max(0, Math.ceil(count / rowsPerPage) - 1));
        };

        return (
            <div style={{ flexShrink: 0 }}>
                <Button onClick={handleFirstPageButtonClick} disabled={page === 0}>
                    {'<<'}
                </Button>
                <Button onClick={handleBackButtonClick} disabled={page === 0}>
                    {'<'}
                </Button>
                <Button onClick={handleNextButtonClick} disabled={page >= Math.ceil(count / rowsPerPage) - 1}>
                    {'>'}
                </Button>
                <Button onClick={handleLastPageButtonClick} disabled={page >= Math.ceil(count / rowsPerPage) - 1}>
                    {'>>'}
                </Button>
            </div>
        );
    };

    return (
        <Box
            sx={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                padding: 2,
                height: '100vh',
            }}
        >
            {/* Contenedor para búsqueda y botón */}
            <Grid container spacing={2} alignItems="center" sx={{ marginBottom: '20px' }}>
                <Grid item>
                    <TextField
                        label="Buscar Usuario"
                        variant="outlined"
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}  // Actualiza el estado de búsqueda
                        sx={{ width: 300 }}  // Ajusta el tamaño del campo de búsqueda
                    />
                </Grid>
                <Grid item>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleCrearUsuario}
                    >
                        Crear Usuario
                    </Button>
                </Grid>
            </Grid>

            <Paper
                sx={{
                    width: '100%',
                    maxWidth: 800,
                    padding: 2,
                }}
            >
                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Nombre</TableCell>
                                <TableCell>Apellido</TableCell>
                                <TableCell>Usuario</TableCell>
                                <TableCell>Estado</TableCell>
                                <TableCell>Fecha Creación</TableCell>
                                <TableCell>Acciones</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {usuarios.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((usuario) => (
                                <TableRow key={usuario.id}>
                                    <TableCell>{usuario.u_nombre}</TableCell>
                                    <TableCell>{usuario.u_apellido}</TableCell>
                                    <TableCell>{usuario.u_usuario}</TableCell>
                                    <TableCell>{usuario.u_estado === 1 ? 'Activo' : 'Inactivo'}</TableCell>
                                    <TableCell>{new Date(usuario.u_fechacreacion).toLocaleDateString()}</TableCell>
                                    <TableCell>
                                        {/* Aquí podrías agregar botones de editar o eliminar */}
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>

                <TablePagination
                    rowsPerPageOptions={[5, 10, 25]}
                    component="div"
                    count={usuarios.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handlePageChange}
                    onRowsPerPageChange={handleRowsPerPageChange}
                    ActionsComponent={CustomPaginationActions}  // Usar nuestra paginación personalizada
                />
            </Paper>
        </Box>
    );
}

export default Usuario;
