import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [nombreProyecto, setNombreProyecto] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [presupuesto, setPresupuesto] = useState('');
  const [estado, setEstado] = useState('');
  const [responsable, setResponsable] = useState('');
  const [hitos, setHitos] = useState([]); // Inicializar como array vacío
  const [resultadoEsperado, setResultadoEsperado] = useState('');
  const [pruebaRealizada, setPruebaRealizada] = useState('');
  const [estadoPrueba, setEstadoPrueba] = useState('');
  const [hitoSeleccionado, setHitoSeleccionado] = useState(''); // Nuevo estado para el hito seleccionado
  const [descripcionHito, setDescripcionHito] = useState(''); // Estado para la descripción del hito
  const [formularioHabilitado, setFormularioHabilitado] = useState(false); // Estado para habilitar solo los campos debajo del primer botón
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el ID del proyecto desde la URL

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    }
  }, [navigate]);

  // Obtener los datos del proyecto
  useEffect(() => {
    const fetchProyecto = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/proyectos/proyecto/${id}`);
        const proyecto = response.data;

        // Asegúrate de convertir las fechas al formato correcto: YYYY-MM-DD
        const formatoFechaInicio = new Date(proyecto.fecha_inicio).toISOString().split('T')[0];
        const formatoFechaFin = new Date(proyecto.fecha_finalizacion).toISOString().split('T')[0];

        setNombreProyecto(proyecto.nombre);
        setDescripcion(proyecto.descripcion);
        setFechaInicio(formatoFechaInicio);
        setFechaFin(formatoFechaFin);
        setPresupuesto(proyecto.presupuesto);
        setEstado(proyecto.estado);
        setResponsable(proyecto.u_usuario);

        // Obtener los hitos del proyecto, asegurándote de que siempre sea un array
        setHitos(proyecto.hitos || []); // Si 'hitos' no existe, asigna un array vacío
      } catch (error) {
        console.error('Error al obtener el proyecto:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los datos del proyecto.',
        });
      }
    };

    if (id) {
      fetchProyecto();
    }
  }, [id]);

  // Obtener los hitos de la API
  useEffect(() => {
    const fetchHitos = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/pruebas/hitos/${id}`);
        setHitos(response.data || []); // Asegúrate de que siempre sea un array
      } catch (error) {
        console.error('Error al obtener los hitos:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los hitos del proyecto.',
        });
      }
    };

    if (id) {
      fetchHitos();
    }
  }, [id]);

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples para el plan de prueba
    if (!resultadoEsperado || !pruebaRealizada || !estadoPrueba || !hitoSeleccionado || !descripcion) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos del plan de prueba son obligatorios.',
      });
      return;
    }

    const planDePrueba = {
      resultadoEsperado,
      pruebaRealizada,
      estadoPrueba,
      hitoSeleccionado, // Incluir el hito seleccionado
      descripcion, // Descripción añadida al plan de prueba
    };

    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      // Aquí puedes enviar el plan de prueba a la API
      const response = await axios.post(`http://localhost:5000/api/proyectos/crearPlanDePrueba/${id}`, planDePrueba, config);

      if (response.data.msg === 'Plan de prueba creado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg,
        });

        // Redirigir después de 2 segundos
        setTimeout(() => {
          navigate('/proyectos/tareas');
        }, 2000);
      }
    } catch (error) {
      console.error('Error al crear el plan de prueba:', error.response || error);

      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el plan de prueba. Inténtalo de nuevo.',
        });
      }
    }
  };

  // Habilitar solo los campos debajo del primer botón
  const handleEnableForm = () => {
    setFormularioHabilitado(true);
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom align="center">
        Crear Plan de Prueba
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Información del Proyecto - Solo Lectura */}
          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              label="Nombre del Proyecto"
              value={nombreProyecto}
              disabled={true}  // Mantener deshabilitado
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              disabled={true}  // Mantener deshabilitado
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              label="Fecha de Inicio"
              type="date"
              value={fechaInicio}
              disabled={true}  // Mantener deshabilitado
              variant="outlined"
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              label="Fecha de Fin"
              type="date"
              value={fechaFin}
              disabled={true}  // Mantener deshabilitado
              variant="outlined"
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              label="Presupuesto"
              value={presupuesto}
              disabled={true}  // Mantener deshabilitado
              type="number"
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              label="Estado del Proyecto"
              value={estado}
              disabled={true}  // Mantener deshabilitado
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              label="Responsable"
              value={responsable}
              disabled={true}  // Mantener deshabilitado
              variant="outlined"
            />
          </Grid>

          {/* Botón para habilitar formulario */}
          <Grid item xs={12}>
            <Button
              fullWidth
              variant="contained"
              color="primary"
              onClick={handleEnableForm}
            >
              Crear Plan de Prueba
            </Button>
          </Grid>

          {/* Otros campos habilitados */}
          {formularioHabilitado && (
            <>
              {/* Campo de selección de hitos */}
              <Grid item xs={12}>
                <InputLabel>Seleccionar Hito</InputLabel>
                <Select
                  value={hitoSeleccionado}
                  onChange={(e) => setHitoSeleccionado(e.target.value)}
                  fullWidth
                  required
                >
                  {hitos.map((hito) => (
                    <MenuItem key={hito.id_hito} value={hito.id_hito}>
                      {hito.nombre_hito}
                    </MenuItem>
                  ))}
                </Select>
              </Grid>

              {/* Campo de descripción */}
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Descripción"
                  value={descripcion}
                  onChange={(e) => setDescripcion(e.target.value)}
                  variant="outlined"
                  required
                />
              </Grid>

              {/* Otros campos */}
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Resultado Esperado"
                  value={resultadoEsperado}
                  onChange={(e) => setResultadoEsperado(e.target.value)}
                  variant="outlined"
                  required
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Prueba Realizada"
                  value={pruebaRealizada}
                  onChange={(e) => setPruebaRealizada(e.target.value)}
                  variant="outlined"
                  required
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Estado de la Prueba"
                  value={estadoPrueba}
                  onChange={(e) => setEstadoPrueba(e.target.value)}
                  variant="outlined"
                  required
                />
              </Grid>

              {/* Botón para enviar formulario */}
              <Grid item xs={12}>
                <Button fullWidth variant="contained" color="primary" type="submit">
                  Enviar Plan de Prueba
                </Button>
              </Grid>
            </>
          )}
        </Grid>
      </form>
    </Box>
  );
};

export default CrearPlanDePrueba;
