import React, { useEffect, useState, useCallback } from 'react';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import SecurityIcon from '@mui/icons-material/Security';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { Routes, Route, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import Usuario from '../pages/Seguridad/usuario';  // Asegúrate de que la ruta sea correcta
import crearUsuario from '../pages/Seguridad/CrearUsuario';  // Asegúrate de que la ruta sea correcta

// Definimos los iconos para las secciones principales del menú
const iconMap = {
    Seguridad: <SecurityIcon />,
    Roles: <PersonIcon />,
    Usuarios: <DescriptionIcon />,
    Permisos: <LockIcon />,
};

// Crear el tema para la interfaz de usuario
const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    // Función para obtener el menú desde la API
    const fetchMenus = useCallback(async () => {
        if (!token) return;  // Si no hay token, no realizamos la petición

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            // Realizamos la petición a la API para obtener el menú
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            console.log('Menú obtenido:', response.data); // Puedes eliminar este log luego de probar
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error al obtener los menús:', error);
        }
    }, [token]);

    // Efecto para verificar autenticación y cargar los menús
    useEffect(() => {
        if (!isAuthenticated) {
            console.log('Usuario no autenticado, redirigiendo al login.');
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    // Función que maneja el clic en el menú para redirigir
    const handleMenuClick = (url) => {
        if (url) {
            console.log('Navegando a:', url); // Para depuración
            navigate(`/${url}`);
        }
    };

    // Organiza el menú (padres y sub-menús)
    const navigation = menuItems.map((item) => {
        if (item.ParentMenuID === 0) { // Solo los elementos principales (sin padre)
            return {
                segment: item.MenuURL, // Usamos MenuURL para la ruta
                title: item.DisplayName,
                icon: iconMap[item.DisplayName] || <DescriptionIcon />,
                children: menuItems
                    .filter((subItem) => subItem.ParentMenuID === item.MenuID)  // Filtramos los sub-items
                    .map((subItem) => ({
                        segment: subItem.MenuURL, // Sub-elementos de menú
                        title: subItem.DisplayName,
                        icon: iconMap[subItem.DisplayName] || <DescriptionIcon />,
                        onClick: () => handleMenuClick(subItem.MenuURL),
                    })),
                onClick: () => handleMenuClick(item.MenuURL),
            };
        }
        return null;
    }).filter(Boolean); // Filtramos elementos vacíos

    return (
        <AppProvider navigation={navigation} theme={demoTheme}>
            <DashboardLayout>
                {/* Aquí las rutas dinámicas dentro del Dashboard */}
                <Routes>
                    {/* Define las rutas de cada sección */}
                    <Route path="seguridad/usuarios" element={<Usuario />} />
                    <Route path="seguridad/crear-usuario" element={<CrearUsuario />} />
                    {/* Aquí podrías agregar más rutas dinámicas para roles, permisos, etc. */}
                </Routes>
            </DashboardLayout>
        </AppProvider>
    );
}
