import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import SecurityIcon from '@mui/icons-material/Security';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import UsuarioForm from '../pages/Seguridad/'; // Asegúrate de que esta ruta sea correcta

const demoTheme = createTheme({
  palette: {
    mode: 'light',
  },
});

// Mapa de íconos según el DisplayName o MenuID
const iconMap = {
  Seguridad: <SecurityIcon />,
  Roles: <DescriptionIcon />,
  Usuarios: <PersonIcon />,
  Permisos: <LockIcon />,
};

function DemoPageContent() {
  return (
    <Box
      sx={{
        py: 35,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <Typography variant="h4">Bienvenido al Gestor de Control de Calidad!</Typography>
    </Box>
  );
}

export default function DashboardLayoutNavigationLinks() {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;
  const [searchParams] = useSearchParams(); // Obtiene los parámetros de búsqueda
  const [menuItems, setMenuItems] = React.useState([]);
  const [currentComponent, setCurrentComponent] = React.useState(null); // Componente actual a mostrar
  const isAuthenticated = Boolean(localStorage.getItem('token')); // Verifica si hay un token

  const fetchMenus = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    const decoded = jwtDecode(token);
    const username = decoded.user.username;

    try {
      const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
      setMenuItems(response.data);
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  React.useEffect(() => {
    fetchMenus();
  }, []);

  // Crear la estructura del menú
  const navigation = menuItems.map(item => {
    if (item.ParentMenuID === 0) {
      return {
        segment: item.DisplayName,
        title: item.DisplayName,
        icon: iconMap[item.DisplayName] || <DescriptionIcon />,
        children: menuItems.filter(subItem => subItem.ParentMenuID === item.MenuID),
        onClick: () => {
          if (!isAuthenticated) {
            navigate('/login'); // Si no está autenticado, redirigir al login
            return;
          }
          if (item.DisplayName.toLowerCase() === 'usuarios') {
            setCurrentComponent(<UsuarioForm />); // Mostrar el formulario de usuario en lugar de navegar
          } else {
            navigate(item.MenuURL); // Navegar a la URL especificada
            setCurrentComponent(null); // Restablecer el componente actual si no es "Usuarios"
          }
        },
      };
    }
    return null;
  }).filter(Boolean);

  return (
    <AppProvider
      navigation={navigation.map(item => ({
        segment: item.segment,
        title: item.title,
        icon: item.icon,
        children: item.children.length > 0 ? item.children.map(subItem => ({
          segment: subItem.DisplayName,
          title: subItem.DisplayName,
          icon: iconMap[subItem.DisplayName] || <DescriptionIcon />,
          onClick: item.onClick, // Navegar al submenú
        })) : [],
      }))}
      router={{
        pathname,
        navigate: (path) => navigate(path),
        searchParams, // Agrega esto
      }}
      theme={demoTheme}
    >
      <DashboardLayout>
        {currentComponent || <DemoPageContent pathname={pathname} />} {/* Muestra el formulario o el contenido demo */}
      </DashboardLayout>
    </AppProvider>
  );
}
