import React, { useEffect, useState, useCallback } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import SecurityIcon from '@mui/icons-material/Security';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import jwtDecode from 'jwt-decode';
import UsuarioForm from '../pages/Seguridad/usuario';

const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

const iconMap = {
    Seguridad: <SecurityIcon />,
    Roles: <DescriptionIcon />,
    Usuarios: <PersonIcon />,
    Permisos: <LockIcon />,
};

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const location = useLocation();
    const pathname = location.pathname;
    const [searchParams] = useSearchParams();
    const [menuItems, setMenuItems] = useState([]);
    const [currentComponent, setCurrentComponent] = useState(null);
    const token = localStorage.getItem('token'); // Token desde localStorage
    const isAuthenticated = Boolean(token); // Verifica si hay un token

    const fetchMenus = useCallback(async () => {
        if (!token) return; // Si no hay token, no hace nada

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    }, [token]); // Agregamos token como dependencia

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login'); // Redirige si no está autenticado
        } else {
            fetchMenus(); // Carga los menús si está autenticado
        }
    }, [isAuthenticated, fetchMenus, navigate, token]);

    // Función para manejar el clic en el menú
    const handleMenuClick = (item) => {
        if (item.DisplayName.toLowerCase() === 'usuarios') {
            setCurrentComponent(<UsuarioForm />);
        } else {
            // Aquí puedes añadir otros formularios o componentes según el menú
            setCurrentComponent(null); // Establece null para navegación estándar
            navigate(item.MenuURL);
        }
    };

    const navigation = menuItems.map(item => {
        if (item.ParentMenuID === 0) {
            return {
                segment: item.DisplayName,
                title: item.DisplayName,
                icon: iconMap[item.DisplayName] || <DescriptionIcon />,
                children: menuItems
                    .filter(subItem => subItem.ParentMenuID === item.MenuID)
                    .map(subItem => ({
                        segment: subItem.DisplayName,
                        title: subItem.DisplayName,
                        icon: iconMap[subItem.DisplayName] || <DescriptionIcon />,
                        onClick: () => handleMenuClick(subItem), // Maneja clic en submenú
                    })),
                onClick: () => handleMenuClick(item), // Maneja clic en menú principal
            };
        }
        return null;
    }).filter(Boolean);

    return (
        <AppProvider
            navigation={navigation}
            router={{
                pathname,
                navigate: (path) => navigate(path),
                searchParams,
            }}
            theme={demoTheme}
        >
            <DashboardLayout>
                {currentComponent || <DemoPageContent />}
            </DashboardLayout>
        </AppProvider>
    );
}

function DemoPageContent() {
    return (
        <Box
            sx={{
                py: 35,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                textAlign: 'center',
            }}
        >
            <Typography variant="h4">Bienvenido al Gestor de Control de Calidad!</Typography>
        </Box>
    );
}
