import React, { useState, useEffect } from 'react';
import { Box, TextField, MenuItem, Select, InputLabel, FormControl, Button, Grid, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const CrearRoles = () => {    

  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const navigate = useNavigate();
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rol, setRol] = useState('');
  const [roles, setRoles] = useState([]);

  // Verificar si el usuario está autenticado
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    } else {
      fetchRoles(); // Si está autenticado, carga los roles
    }
  }, [navigate]);

  // Obtener los roles disponibles desde el backend
  const fetchRoles = async () => {
    try {
      const response = await axios.get('/api/roles'); // URL del endpoint para obtener roles
      setRoles(response.data); // Guardamos los roles en el estado
    } catch (error) {
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los roles disponibles.'
      });
    }
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples
    if (!nombre || !apellido || !username || !password || !rol) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.'
      });
      return;
    }

    const newRole = {
      nombre,
      apellido,
      username,
      password,
      rol
    };

    try {
      // Enviar la solicitud al backend para crear un nuevo rol
      const response = await axios.post('/api/crear-rol', newRole); // URL del endpoint para crear roles
      setShowAlert({
        severity: 'success',
        message: 'Rol creado exitosamente.'
      });

      // Limpiar el formulario después de un éxito
      setNombre('');
      setApellido('');
      setUsername('');
      setPassword('');
      setRol('');
    } catch (error) {
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al crear el rol.'
      });
    }
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h4" gutterBottom>
        Crear Nuevo Rol
      </Typography>

      {/* Mostrar el mensaje de alerta */}
      {showAlert && <AlertMessage severity={showAlert.severity} message={showAlert.message} />}

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Apellido"
              value={apellido}
              onChange={(e) => setApellido(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre de Usuario"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Contraseña"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth required>
              <InputLabel>Rol</InputLabel>
              <Select
                value={rol}
                onChange={(e) => setRol(e.target.value)}
              >
                {roles.map((role) => (
                  <MenuItem key={role.id} value={role.id}>
                    {role.nombre}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
            >
              Crear Rol
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default CrearRoles;
