import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box } from '@mui/material';
import { styled } from '@mui/system';
import { useNavigate } from 'react-router-dom'; // Para redirigir
import logo from '../assets/LogoPrincipal.png'; // Asegúrate de que la ruta sea correcta

// Estilo para el logo circular
const CircularLogo = styled('img')({
  width: '350px', // Ajusta el tamaño según sea necesario
  height: '200px',
  borderRadius: '50%', // Esto hace que el logo sea circular
  display: 'block',
  margin: '0 auto', // Centra el logo
});

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate(); // Hook para redirigir

  // Validación en tiempo real
  const handleUsuarioChange = (e) => {
    setUsuario(e.target.value);
    if (e.target.value) {
      setErrors((prevErrors) => ({ ...prevErrors, usuario: '' })); // Elimina el error de usuario si está llenado
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Validación básica
    const newErrors = {};
    if (!usuario) newErrors.usuario = 'El usuario es requerido';
    if (!password) newErrors.password = 'La contraseña es requerida';

    if (Object.keys(newErrors).length > 0) {
        setErrors(newErrors);
        return; // Detenemos el envío del formulario si hay errores
    }

    // Aquí puedes agregar la lógica para manejar el inicio de sesión
    try {
        const response = await fetch('http://localhost:5000/api/auth/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username: usuario, password }),
        });

        // Verifica si la respuesta es exitosa
        if (!response.ok) {
            const errorData = await response.json(); // Obtén el mensaje de error
            throw new Error(errorData.message || 'Error en la conexión.'); // Proporciona un mensaje por defecto
        }

        const data = await response.json(); // Si la respuesta es exitosa, procesamos el JSON
        console.log("Inicio de sesión exitoso", data);

        // Redirigir al usuario a la página de inicio
        navigate('/home'); // Asegúrate de que esta ruta exista y sea correcta

    } catch (error) {
        console.error('Error al iniciar sesión:', error.message);
        // Muestra un mensaje de error al usuario
        setErrors({ submit: error.message });
    }
};

  return (
    <Container maxWidth="xs" sx={{ mt: 8 }}>
      <CircularLogo src={logo} alt="Logo" />
      <Typography variant="h4" align="center" gutterBottom>
        Iniciar Sesión
      </Typography>
      <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
        <TextField
          required
          fullWidth
          label="Usuario" // "Usuario"
          margin="normal"
          value={usuario}
          onChange={handleUsuarioChange} // Validación en tiempo real
          error={Boolean(errors.usuario)}
          helperText={errors.usuario}
        />
        <TextField
          required
          fullWidth
          label="Contraseña" // "Contraseña"
          type="password"
          margin="normal"
          value={password}
          onChange={handlePasswordChange} // Validación en tiempo real
          error={Boolean(errors.password)}
          helperText={errors.password}
        />
        {errors.api && (
          <Typography color="error" variant="body2">
            {errors.api}
          </Typography>
        )}
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Iniciar Sesión
        </Button>
      </Box>
    </Container>
  );
};

export default Login;
