import { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import AppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Typography from '@mui/material/Typography';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import { Link, Outlet } from 'react-router-dom';
import axios from 'axios';

// Importar íconos de Material-UI
import GroupIcon from '@mui/icons-material/Group';
import PeopleIcon from '@mui/icons-material/People';
import DescriptionIcon from '@mui/icons-material/Description';

const drawerWidth = 240;

// Mapeo de nombres de íconos a componentes
const iconMapping = {
    'Group': GroupIcon,
    'People': PeopleIcon,
    'Description': DescriptionIcon,
};

const DashboardLayoutNavigationLinks = () => {
    const [menuItems, setMenuItems] = useState([]);
    const [username, setUsername] = useState('');

    useEffect(() => {
        const fetchMenuItems = async () => {
            try {
                const token = localStorage.getItem('token'); // Obtener el token JWT (o de autenticación)
                const response = await axios.get('https://localhost:7240/api/Menu', {
                    headers: { Authorization: `Bearer ${token}` }, // Agregar el token en los encabezados si es necesario
                });
                console.log(response.data); // Verifica los datos que devuelve la API
                setUsername(response.data.username); // Establecer el nombre de usuario
                setMenuItems(response.data.menuItems); // Establecer los elementos del menú
            } catch (error) {
                console.error('Error fetching menu items:', error);
            }
        };

        fetchMenuItems();
    }, []);

    // Función para obtener el componente de ícono correcto
    const getIcon = (iconName) => {
        const formattedIconName = iconName.charAt(0).toUpperCase() + iconName.slice(1);  // Asegúrate de que las mayúsculas coincidan
        const IconComponent = iconMapping[formattedIconName];
        return IconComponent ? <IconComponent /> : null;
    };

    // Función para organizar los elementos del menú (de acuerdo con ParentMenuID)
    const organizeMenuItems = (items) => {
        const parentItems = items.filter(item => item.ParentMenuID === 0);
        return parentItems.map(parent => ({
            ...parent,
            children: items.filter(child => child.ParentMenuID === parent.MenuID)
        }));
    };

    const organizedMenu = organizeMenuItems(menuItems);

    return (
        <Box sx={{ display: 'flex' }}>
            <CssBaseline />
            <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
                <Toolbar>
                    <Typography variant="h6" noWrap component="div">
                        Sistema de Gestión
                    </Typography>
                    <Typography variant="body1" sx={{ ml: 2 }}>
                        {username && `Bienvenido, ${username}`}
                    </Typography>
                </Toolbar>
            </AppBar>
            <Drawer
                variant="permanent"
                sx={{
                    width: drawerWidth,
                    flexShrink: 0,
                    [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
                }}
            >
                <Toolbar />
                <Box sx={{ overflow: 'auto' }}>
                    <List>
                        {organizedMenu.map((parentItem) => (
                            <div key={parentItem.MenuID}>
                                <ListItem disablePadding>
                                    <ListItemButton component={Link} to={`/dashboard/${parentItem.MenuURL}`}>
                                        <ListItemIcon>
                                            {getIcon(parentItem.MenuIcon)}
                                        </ListItemIcon>
                                        <ListItemText primary={parentItem.DisplayName} />
                                    </ListItemButton>
                                </ListItem>
                                {parentItem.children.map((childItem) => (
                                    <ListItem key={childItem.MenuID} disablePadding sx={{ pl: 4 }}>
                                        <ListItemButton component={Link} to={`/dashboard/${childItem.MenuURL}`}>
                                            <ListItemIcon>
                                                {getIcon(childItem.MenuIcon)}
                                            </ListItemIcon>
                                            <ListItemText primary={childItem.DisplayName} />
                                        </ListItemButton>
                                    </ListItem>
                                ))}
                            </div>
                        ))}
                    </List>
                </Box>
            </Drawer>
            <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                <Toolbar />
                <Outlet />
            </Box>
        </Box>
    );
};

export default DashboardLayoutNavigationLinks;
