import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';

const demoTheme = createTheme({
  palette: {
    mode: 'light',
  },
});

function DemoPageContent({ pathname }) {
  return (
    <Box
      sx={{
        py: 4,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <Typography>Dashboard content for {pathname}</Typography>
    </Box>
  );
}

export default function DashboardLayoutNavigationLinks() {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;
  const [menuItems, setMenuItems] = React.useState([]);
  const [openMenu, setOpenMenu] = React.useState(null); // Estado para manejar qué menú está abierto

  const fetchMenus = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    const decoded = jwtDecode(token);
    const username = decoded.user.username;

    try {
      const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
      setMenuItems(response.data);
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  React.useEffect(() => {
    fetchMenus();
  }, []);

  // Crear la estructura del menú
  const navigation = menuItems.reduce((acc, item) => {
    if (item.ParentMenuID === 0) {
      // Elemento principal
      acc.push({
        ...item,
        children: menuItems.filter(subItem => subItem.ParentMenuID === item.MenuID),
      });
    }
    return acc;
  }, []);

  const handleToggle = (menuID) => {
    setOpenMenu(openMenu === menuID ? null : menuID); // Alterna el estado abierto
  };

  return (
    <AppProvider
      navigation={navigation.map(item => ({
        segment: item.DisplayName,
        title: item.DisplayName,
        icon: <ExpandMoreIcon />,
        children: item.children.map(subItem => ({
          segment: subItem.DisplayName,
          title: subItem.DisplayName,
          icon: <DescriptionIcon />,
          onClick: () => navigate(subItem.MenuURL),
        })),
      }))}
      router={{
        pathname,
        navigate: (path) => navigate(path),
      }}
      theme={demoTheme}
    >
      <DashboardLayout>
        <Box sx={{ display: 'flex', flexDirection: 'column', width: '250px' }}>
          {navigation.map(item => (
            <div key={item.MenuID}>
              <ListItemButton onClick={() => handleToggle(item.MenuID)}>
                <ListItemText primary={item.DisplayName} />
                <ExpandMoreIcon />
              </ListItemButton>
              <Collapse in={openMenu === item.MenuID} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  {item.children.map(subItem => (
                    <ListItemButton key={subItem.MenuID} onClick={() => navigate(subItem.MenuURL)}>
                      <ListItemText primary={subItem.DisplayName} />
                    </ListItemButton>
                  ))}
                </List>
              </Collapse>
            </div>
          ))}
        </Box>
        <DemoPageContent pathname={pathname} />
      </DashboardLayout>
    </AppProvider>
  );
}
