import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login'; 
import { isAuthenticated } from './utils/auth'; 
import Usuario from './pages/Seguridad/usuario';
import Dashboard from './pages/Dashboard';

function ProtectedRoute({ children }) {
    if (!isAuthenticated()) {
        return <Navigate to="/login" />;
    }
    return children;
}

function App() {
    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route path="/login" element={<Login />} />

                {/* Ruta para el Dashboard, que requiere autenticación */}
                <Route 
                    path="/" 
                    element={<ProtectedRoute><Dashboard /></ProtectedRoute>} 
                >
                    {/* Rutas internas del Dashboard */}
                    <Route path="seguridad/usuarios" element={<Usuario />} />
                    {/* Puedes añadir más rutas internas aquí */}
                    {/* <Route path="seguridad/roles" element={<Roles />} /> */}
                </Route>

                {/* Redirigir cualquier ruta no encontrada al login */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
