import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend
} from 'chart.js';
import { jwtDecode } from 'jwt-decode';

// Registrar las partes necesarias de Chart.js
ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardCharts = () => {
    const [proyectos, setProyectos] = useState({
        labels: [],
        datasets: [],
        vencidos: 0,
        noVencidos: 0,
        vencidosNombres: [],
        noVencidosNombres: []
    });
    const [loading, setLoading] = useState(true);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };
    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        const decoded = jwtDecode(token);
        const id = decoded.user.id;

        try {
            const response = await axios.get(`http://localhost:5000/api/grafica/VencimientoProyecto/${id}`, config);
            const { labels, vencidos, noVencidos, vencidosNombres, noVencidosNombres } = response.data;

            // Configurar los datos correctamente para react-chartjs-2
            setProyectos({
                labels,
                datasets: [
                    {
                        data: [vencidos, noVencidos],
                        backgroundColor: ['#FF6384', '#36A2EB'],
                    }
                ],
                vencidos,
                noVencidos,
                vencidosNombres,
                noVencidosNombres
            });
            setLoading(false);
        } catch (error) {
            console.error('Error al obtener los proyectos:', error);
            setLoading(false);
        }
    }, [isAuthenticated, token]);

    useEffect(() => {
        if (isAuthenticated) {
            fetchProyectos();
        }
    }, [isAuthenticated, fetchProyectos]);

    if (loading) return <div>Loading...</div>;

    if (!proyectos.labels.length || !proyectos.datasets.length) {
        return <div>El usuario no tiene proyectos asignados o en procesos</div>;
    }

    return (
        <div>
            <h2>Proyectos Pr√≥ximos a Vencer</h2>
            <Pie
                data={{
                    labels: proyectos.labels,
                    datasets: proyectos.datasets
                }}
                options={{
                    responsive: true,
                    plugins: {
                        legend: {
                            position: 'top',
                        },
                        tooltip: {
                            callbacks: {
                                label: (tooltipItem) => {
                                    const index = tooltipItem.dataIndex;
                                    const projectNames =
                                        index === 0
                                            ? proyectos.vencidosNombres
                                            : proyectos.noVencidosNombres;
                                    return `${tooltipItem.raw} Proyectos: ${projectNames.join(", ")}`;
                                }
                            }
                        }
                    }
                }}
            />
        </div>
    );
};

export default DashboardCharts;
