import React, { useState, useEffect } from 'react';
import {
  Box,
  TextField,
  Button,
  Grid,
  Typography,
  MenuItem,
  Select,
  InputLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableFooter,
  TablePagination,
  TableRow,
  TableHead,
  Paper,
  IconButton,
  Dialog,
  DialogContent,
} from '@mui/material';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import VisibilityIcon from '@mui/icons-material/Visibility';

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null);
  const [hitos, setHitos] = useState([]);
  const [hitoSeleccionado, setHitoSeleccionado] = useState('');
  const [descripcionHito, setDescripcionHito] = useState('');
  const [casos, setCasos] = useState([]);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [openImageDialog, setOpenImageDialog] = useState(false);
  const [selectedImage, setSelectedImage] = useState('');
  const [casoSeleccionado, setCasoSeleccionado] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchHitos = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/pruebas/hitosporPruebas/${id}`);
        setHitos(response.data || []);
      } catch (error) {
        console.error('Error al obtener los hitos:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los hitos del proyecto.',
        });
      }
    };

    if (id) {
      fetchHitos();
    }
  }, [id]);

  const handleHitoSeleccionado = (e) => {
    const hitoId = e.target.value;
    setHitoSeleccionado(hitoId);

    const hito = hitos.find((hito) => hito.id_hito === hitoId);
    setDescripcionHito(hito ? hito.descripcion : '');
  };

  const handleVerCasos = async () => {
    if (!hitoSeleccionado) {
      setShowAlert({
        severity: 'warning',
        message: 'Por favor, selecciona un hito.',
      });
      return;
    }

    try {
      const response = await axios.get(`http://localhost:5000/api/pruebas/casos/${id}/${hitoSeleccionado}`);
      setCasos(response.data || []);
      setShowAlert({
        severity: 'success',
        message: 'Casos cargados exitosamente.',
      });
    } catch (error) {
      console.error('Error al obtener los casos:', error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los casos del hito.',
      });
    }
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const filteredCasos = casos.filter((caso) => {
    return (
      caso.resultado_esperado?.toLowerCase().includes(search.toLowerCase()) ||
      caso.prueba_realizada?.toLowerCase().includes(search.toLowerCase())
    );
  });

  const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - filteredCasos.length) : 0;

  const handleOpenImageDialog = (image) => {
    setSelectedImage(image);
    setOpenImageDialog(true);
  };

  const handleCloseImageDialog = () => {
    setOpenImageDialog(false);
    setSelectedImage('');
  };

  // Close alert after 3 seconds if it is shown
  useEffect(() => {
    if (showAlert) {
      const timer = setTimeout(() => {
        setShowAlert(null);
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [showAlert]);

  // Cambiar el estado del caso seleccionado
  const handleCambiarEstado = async () => {
    if (!casoSeleccionado) {
 setShowAlert({
        severity: 'warning',
        message: 'Por favor, selecciona un caso.',
      });
      return;
    }

    try {
      const response = await axios.put(`http://localhost:5000/api/pruebas/casos/${casoSeleccionado.id_plan}`, {
        estado_prueba: "nuevo_estado", // Cambia esto por el nuevo estado que deseas establecer
      });

      // Actualiza el estado local si es necesario
      const updatedCasos = casos.map(caso => {
        if (caso.id_plan === casoSeleccionado.id_plan) {
          return { ...caso, estado_prueba: response.data.estado_prueba }; // Asegúrate de que la respuesta tenga el nuevo estado
        }
        return caso;
      });

      setCasos(updatedCasos);
      setShowAlert({
        severity: 'success',
        message: 'Estado cambiado exitosamente.',
      });
      setCasoSeleccionado(null); // Limpiar la selección
    } catch (error) {
      console.error('Error al cambiar el estado:', error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cambiar el estado del caso.',
      });
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      <Typography variant="h4" gutterBottom align="center">
        Plan de Pruebas
      </Typography>

      {/* Selección de hitos */}
      <InputLabel id="hito-label">Selecciona un Hito</InputLabel>
      <Select
        labelId="hito-label"
        value={hitoSeleccionado}
        onChange={handleHitoSeleccionado}
        fullWidth
      >
        {hitos.map((hito) => (
          <MenuItem key={hito.id_hito} value={hito.id_hito}>
            {hito.nombre_hito}
          </MenuItem>
        ))}
      </Select>
      <Button variant="contained" color="primary" onClick={handleVerCasos}>
        Ver Casos
      </Button>

      {/* Botón para cambiar el estado */}
      <Button 
        variant="contained" 
        color="primary" 
        onClick={handleCambiarEstado} 
        sx={{ position: 'absolute', top: 16, right: 16 }} // Posiciona el botón en la parte superior derecha
      >
        Cambiar Estado
      </Button>

      {/* Mostrar AlertMessage */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          onClose={() => setShowAlert(null)}
        />
      )}

      {/* Tabla de casos */}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Nombre Hito</TableCell>
              <TableCell>Resultado Esperado</TableCell>
              <TableCell>Prueba Realizada</TableCell>
              <TableCell>Estado Prueba</TableCell>
              <TableCell>Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {(rowsPerPage > 0
              ? filteredCasos.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              : filteredCasos
            ).map((caso) => (
              <TableRow 
                key={caso.id_plan} 
                onClick={() => setCasoSeleccionado(caso)} // Permite seleccionar el caso
                style={{ cursor: 'pointer', backgroundColor: casoSeleccionado?.id_plan === caso.id_plan ? '#f0f0f0' : '' }} // Resalta el caso seleccionado
              >
                <TableCell>{caso.id_plan}</TableCell>
                <TableCell>{caso.nombre_hito}</TableCell>
                <TableCell>{caso.resultado_esperado}</TableCell>
                <TableCell>{caso.prueba_realizada}</TableCell>
                <TableCell>{caso.estado_prueba}</TableCell>
                <TableCell>
                  <IconButton onClick={() => handleOpenImageDialog(caso.imagen_prueba)}>
                    <VisibilityIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            {emptyRows > 0 && (
              <TableRow style={{ height: 53 * emptyRows }}>
                <TableCell colSpan={6} />
              </TableRow>
            )}
          </TableBody>
          <TableFooter>
            <TableRow>
              <TablePagination
                rowsPerPageOptions={[5, 10, 25]}
                count={filteredCasos.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handlePageChange}
                onRowsPerPageChange={handleRowsPerPageChange}
              />
            </TableRow>
          </TableFooter>
        </Table>
      </TableContainer>

      {/* Diálogo para mostrar la imagen de la prueba */}
      <Dialog open={openImageDialog} onClose={handleCloseImageDialog}>
        <DialogContent>
          <img src={selectedImage} alt="Prueba" style={{ width: '100%' }} />
        </DialogContent>
      </Dialog>
    </Box>
  );
};

export default CrearPlanDePrueba;
