import React, { useState } from 'react';
import axios from 'axios';

function CrearPermisos() {
  const [selectedMenus, setSelectedMenus] = useState({});
  const [roleId, setRoleId] = useState('6'); // Asumimos que el roleId es 6 para este ejemplo

  const handleMenuChange = (menuId) => {
    setSelectedMenus((prevSelectedMenus) => ({
      ...prevSelectedMenus,
      [menuId]: !prevSelectedMenus[menuId],
    }));
  };

  const savePermissions = async () => {
    try {
      // Preparar los datos para enviar
      const permissionsToSave = Object.keys(selectedMenus)
        .filter(menuId => selectedMenus[menuId]) // Filtrar solo los menús seleccionados
        .map(menuId => ({
          roleId: roleId,  // Aseguramos que roleId esté primero
          menuId: menuId,
        }));

      // Verificar datos a enviar
      console.log("Datos a enviar:", permissionsToSave);

      // Enviar datos al backend
      const response = await axios.post('http://localhost:5000/api/permissions', {
        permissions: permissionsToSave,
      });

      // Manejo de la respuesta
      if (response.status === 200) {
        alert('Permisos guardados correctamente');
      }
    } catch (error) {
      console.error("Error al guardar permisos:", error);
      alert('Error al guardar permisos');
    }
  };

  return (
    <div>
      <h1>Asignar permisos</h1>
      {/* Ejemplo de checkboxes */}
      <div>
        <input
          type="checkbox"
          checked={selectedMenus['1']}
          onChange={() => handleMenuChange('1')}
        />
        Menú 1
      </div>
      <div>
        <input
          type="checkbox"
          checked={selectedMenus['3']}
          onChange={() => handleMenuChange('3')}
        />
        Menú 3
      </div>
      {/* Aquí puedes agregar más menús */}

      <button onClick={savePermissions}>Guardar permisos</button>
    </div>
  );
}

export default CrearPermisos;
