import React, { useEffect, useState } from 'react';
import { Box, Drawer, List, ListItem, ListItemIcon, ListItemText, Toolbar, Typography } from '@mui/material';
import { Outlet, useNavigate } from 'react-router-dom';

const drawerWidth = 240;

function Dashboard() {
    const [menuItems, setMenuItems] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMenu = async () => {
            const usuarioLogueado = 'usuarioEjemplo'; // Cambia esto por el usuario logueado actual
            try {
                const response = await fetch(`http://localhost:5000/api/navigation/menu/${usuarioLogueado}`);
                const data = await response.json();
                setMenuItems(data);
            } catch (error) {
                console.error('Error al cargar el menú:', error);
            }
        };
        fetchMenu();
    }, []);

    const handleMenuClick = (url) => {
        navigate(`/${url}`);
    };

    return (
        <Box sx={{ display: 'flex' }}>
            <Drawer
                variant="permanent"
                sx={{
                    width: drawerWidth,
                    flexShrink: 0,
                    [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
                }}
            >
                <Toolbar />
                <List>
                    {menuItems.map((item) => (
                        <ListItem button key={item.MenuID} onClick={() => handleMenuClick(item.MenuURL)}>
                            {item.MenuIcon && <ListItemIcon>{/* Aquí podrías agregar el icono dinámico */}</ListItemIcon>}
                            <ListItemText primary={item.DisplayName} />
                        </ListItem>
                    ))}
                </List>
            </Drawer>
            <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                <Toolbar />
                <Outlet />
            </Box>
        </Box>
    );
}

export default Dashboard;
