import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios'; // Asegúrate de que axios esté instalado
import { jwtDecode } from 'jwt-decode'; // Asegúrate de que el nombre sea correcto

const demoTheme = createTheme({
  palette: {
    mode: 'light',
  },
});

function DemoPageContent({ pathname }) {
  return (
    <Box
      sx={{
        py: 4,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <Typography>Dashboard content for {pathname}</Typography>
    </Box>
  );
}

export default function DashboardLayoutNavigationLinks() {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;
  const [menuItems, setMenuItems] = React.useState([]);
  const [open, setOpen] = React.useState(false); // Estado para manejar el menú desplegable

  const fetchMenus = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    const decoded = jwtDecode(token); // Asegúrate de que la estructura del token sea correcta
    const username = decoded.user.username; // Ajusta esto según la estructura de tu token

    try {
      const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`); // Ruta correcta
      setMenuItems(response.data);
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  React.useEffect(() => {
    fetchMenus();
  }, []);

  // Función para manejar el clic en el menú principal
  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen);
  };

  // Crear la estructura del menú
  const navigation = menuItems.map(item => {
    if (item.ParentMenuID === 0) {
      // Elemento principal
      return {
        segment: item.DisplayName,
        title: item.DisplayName, // Cambia esto si tienes títulos diferentes
        icon: <DescriptionIcon />,
        children: menuItems.filter(subItem => subItem.ParentMenuID === item.MenuID), // Submenús
      };
    }
    return null;
  }).filter(Boolean);

  return (
    <AppProvider
      navigation={navigation.map(item => ({
        segment: item.segment,
        title: item.title,
        icon: item.icon,
        children: item.children.length > 0 ? item.children.map(subItem => ({
          segment: subItem.DisplayName,
          title: subItem.DisplayName,
          icon: <DescriptionIcon />, // Cambia esto si es necesario
          onClick: () => navigate(subItem.MenuURL), // Navegar al submenú
        })) : [],
      }))}
      router={{
        pathname,
        navigate: (path) => navigate(path), // Pasando la función de navegación correctamente
      }}
      theme={demoTheme}
    >
      <DashboardLayout>
        <DemoPageContent pathname={pathname} />
      </DashboardLayout>
    </AppProvider>
  );
}
