import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom'; 
import Login from './pages/Login';
import Home from './pages/Home';
import DashboardLayoutNavigationLinks from './components/DashboardLayoutNavigationLinks';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Estado de autenticación

  const handleLogin = () => {
    setIsAuthenticated(true); // Cambia el estado a autenticado
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login onLogin={handleLogin} />} />
        <Route path="/home" element={<Home />} />
        <Route 
          path="/dashboard" 
          element={isAuthenticated ? <DashboardLayoutNavigationLinks /> : <Navigate to="/" />} 
        />
      </Routes>
    </Router>
  );
};

export default App;
