import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login'; 
import { isAuthenticated } from './utils/auth'; 
import Usuario from './pages/Seguridad/usuario';
import Roles from './pages/Seguridad/Roles';
import CrearUsuario from './pages/Seguridad/CrearUsuario';  
import EditarUsuario from './pages/Seguridad/EditarUsuario';
import CrearRoles from './pages/Seguridad/CrearRoles';
import EditarRol from './pages/Seguridad/EditarRol';
import CrearPermisos from './pages/Seguridad/CrearPermisos';
import Proyectos from './pages/Gestiones/Proyectos';
import CrearProyectos from './pages/Gestiones/CrearProyectos';
import EditarProyecto from './pages/Gestiones/EditarProyecto';
import CrearHitos from './pages/Gestiones/CrearHitos';
import EditarHito from './pages/Gestiones/EditarHito';
import Dashboard from './pages/Dashboard';
import Tareas from './pages/Proyectos/Tareas';
import EditarTarea from './pages/p'

function ProtectedRoute({ children }) {
    if (!isAuthenticated()) {
        return <Navigate to="/login" />;
    }
    return children;
}

function App() {
    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route path="/login" element={<Login />} />

                {/* Ruta protegida para el Dashboard */}
                <Route path="/" element={<ProtectedRoute><Dashboard /></ProtectedRoute>}>
                    {/* Rutas internas dentro del Dashboard */}
                    <Route path="seguridad/usuarios" element={<Usuario />} />
                    <Route path="seguridad/crear-usuario" element={<CrearUsuario />} />
                    <Route path="seguridad/editar-usuario/:userId" element={<EditarUsuario />} />
                    <Route path="seguridad/roles" element={<Roles />} />
                    <Route path="seguridad/crear-roles" element={<CrearRoles />} />
                    <Route path="seguridad/editar-roles/:rolId" element={<EditarRol />} />
                    <Route path="seguridad/crear-permisos/:rolId" element={<CrearPermisos />} />

                    {/* Gestion del proyecyo*/}
                    <Route path="gestiones/proyectos" element={<Proyectos />} />
                    <Route path="gestiones/crear-proyectos" element={<CrearProyectos />} />
                    <Route path="gestiones/editar-proyectos/:proyectoId" element={<EditarProyecto />} />
                    <Route path="gestiones/crear-hitos/:proyectoId" element={<CrearHitos />} />
                    <Route path="gestiones/editar-hitos/:id_hito" element={<EditarHito />} />

                    {/* Gestion del proyecyo*/}
                    <Route path="proyectos/tareas" element={<Tareas />} />

                </Route>

                {/* Redirigir cualquier ruta no encontrada al login */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
