import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel, Table, TableBody, TableCell, TableContainer, TableFooter, TablePagination, TableRow, TableHead, Paper } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [hitos, setHitos] = useState([]); // Inicializar como array vacío
  const [hitoSeleccionado, setHitoSeleccionado] = useState(''); // Valor vacío como predeterminado
  const [descripcionHito, setDescripcionHito] = useState(''); // Descripción del hito
  const [casos, setCasos] = useState([]); // Estado para almacenar los casos
  const [search, setSearch] = useState(''); // Estado para el campo de búsqueda
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el ID del proyecto desde la URL

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    const fetchHitos = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/pruebas/hitosporPruebas/${id}`);
        setHitos(response.data || []); // Asegúrate de que siempre sea un array
      } catch (error) {
        console.error('Error al obtener los hitos:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los hitos del proyecto.',
        });
      }
    };

    if (id) {
      fetchHitos();
    }
  }, [id]);

  // Manejar la selección de un hito
  const handleHitoSeleccionado = (e) => {
    const hitoId = e.target.value;
    setHitoSeleccionado(hitoId);

    // Obtener la descripción del hito seleccionado
    const hito = hitos.find((hito) => hito.id_hito === hitoId);
    setDescripcionHito(hito ? hito.descripcion : '');
  };

  // Función para manejar la consulta de casos
  const handleVerCasos = async () => {
    if (!hitoSeleccionado) {
      setShowAlert({
        severity: 'warning',
        message: 'Por favor, selecciona un hito.',
      });
      return;
    }

    try {
      const response = await axios.get(`http://localhost:5000/api/pruebas/casos/${id}/${hitoSeleccionado}`);
      setCasos(response.data || []); // Almacenar los casos obtenidos
    } catch (error) {
      console.error('Error al obtener los casos:', error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los casos del hito.',
      });
    }
  };

  // Handle page change
  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  // Handle rows per page change
  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Filtrar casos por búsqueda
  const filteredCasos = casos.filter(caso => {
    return (
      caso.resultado_esperado.toLowerCase().includes(search.toLowerCase()) ||
      caso.prueba_realizada.toLowerCase().includes(search.toLowerCase())
    );
  });

  const emptyRows =
    page > 0 ? Math.max(0, (1 + page) * rowsPerPage - filteredCasos.length) : 0;

  return (
    <Box sx={{ padding: 5 }}>
      <Typography variant="h4" gutterBottom align="center">
        Test Plan
      </Typography>

      <form>
        <Grid container spacing={2} mt={3}>
          <Grid item xs={12}>
            <InputLabel>Seleccion a un Hito</InputLabel>
            <Select
              fullWidth
              value={hitoSeleccionado}
              onChange={handleHitoSeleccionado}
              displayEmpty
              required
            >
              <MenuItem value="" disabled>Selecciona un hito...</MenuItem>
              {hitos.map((hito) => (
                <MenuItem key={hito.id_hito} value={hito.id_hito}>
                  {hito.nombre_hito}
                </MenuItem>
              ))}
            </Select>
          </Grid>

          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Descripción del Hito"
              value={descripcionHito}
              disabled={true}
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12}>
            <Button variant="contained" fullWidth onClick={handleVerCasos}>
              Ver Casos
            </Button>
          </Grid>

          <Grid item xs={12}>
            <TextField
              label="Buscar Caso"
              variant="outlined"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              sx={{ width: '100%' }}
            />
          </Grid>
        </Grid>
      </form>

      <TableContainer component={Paper} sx={{ marginTop: 3 }}>
        <Table sx={{ minWidth: 500 }} aria-label="custom pagination table">
          <TableHead>
            <TableRow>
              <TableCell>Caso</TableCell>
              <TableCell align="left">Hito</TableCell>
              <TableCell align="left">Resultado Esperado</TableCell>
              <TableCell align="left">Prueba Realizada</TableCell>
              <TableCell align="left">Prueba Realizada</TableCell>

            </TableRow>
          </TableHead>
          <TableBody>
            {(rowsPerPage > 0
              ? filteredCasos.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              : filteredCasos
            ).map((caso) => (
              <TableRow key={caso.id_plan}>
                <TableCell component="th" scope="row">
                  {caso.id_plan}
                </TableCell>
                <TableCell align="left">{caso.hito_seleccionado}</TableCell>
                <TableCell align="left">{caso.resultado_esperado}</TableCell>
                <TableCell align="left">{caso.prueba_realizada}</TableCell>
              </TableRow>
            ))}
            {emptyRows > 0 && (
              <TableRow style={{ height: 53 * emptyRows }}>
                <TableCell colSpan={4} />
              </TableRow>
            )}
          </TableBody>
          <TableFooter>
            <TableRow>
              <TablePagination
                rowsPerPageOptions={[5, 10, 25, { label: 'Todos', value: -1 }]}
                colSpan={3}
                count={filteredCasos.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handlePageChange}
                onRowsPerPageChange={handleRowsPerPageChange}
              />
            </TableRow>
          </TableFooter>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default CrearPlanDePrueba;
