import React, { useEffect, useState, useCallback } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import SecurityIcon from '@mui/icons-material/Security';
import axios from 'axios';
import Usuario from '../pages/Seguridad/usuario'; // Asegúrate de que la ruta sea correcta
import Roles from '../pages/Seguridad/roles'; // Ejemplo de otra página
import Permisos from '../pages/Seguridad/permisos'; // Ejemplo de otra página

// Definimos los iconos para el menú
const iconMap = {
    Seguridad: <SecurityIcon />,
    Roles: <PersonIcon />,
    Usuarios: <DescriptionIcon />,
    Permisos: <LockIcon />,
};

// Crear un tema para la aplicación
const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    // Función para obtener el menú de la API
    const fetchMenus = useCallback(async () => {
        if (!token) return;

        try {
            // Aquí estás obteniendo el menú desde tu API
            const response = await axios.get('http://localhost:5000/api/navigation/menu');
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    }, [token]);

    // Fetch de los menús cuando el componente se monta
    useEffect(() => {
        if (isAuthenticated) {
            fetchMenus();
        } else {
            navigate('/login'); // Redirige a login si no está autenticado
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    // Función que maneja la navegación cuando el usuario hace clic en una opción de menú
    const handleMenuClick = (url) => {
        navigate(`/${url}`); // Navega a la URL proporcionada
    };

    // Organiza el menú para incluir las opciones y sus sub-opciones
    const navigation = menuItems.map((item) => {
        if (item.ParentMenuID === 0) {
            return {
                segment: item.MenuURL, // La URL del menú
                title: item.DisplayName,
                icon: iconMap[item.DisplayName] || <DescriptionIcon />,
                children: menuItems
                    .filter((subItem) => subItem.ParentMenuID === item.MenuID)
                    .map((subItem) => ({
                        segment: subItem.MenuURL, // La URL de las sub-opciones
                        title: subItem.DisplayName,
                        icon: iconMap[subItem.DisplayName] || <DescriptionIcon />,
                        onClick: () => handleMenuClick(subItem.MenuURL),
                    })),
                onClick: () => handleMenuClick(item.MenuURL),
            };
        }
        return null;
    }).filter(Boolean); // Filtra los items que no tienen ParentMenuID igual a 0

    return (
        <Box>
            {/* Aquí el encabezado del Dashboard */}
            <Typography variant="h4" sx={{ mb: 3 }}>
                Bienvenido al Dashboard
            </Typography>

            {/* Aquí las rutas internas del Dashboard */}
            <Routes>
                {/* Define las rutas de cada sección */}
                <Route path="seguridad/usuarios" element={<Usuario />} />
                <Route path="seguridad/roles" element={<Roles />} />
                <Route path="seguridad/permisos" element={<Permisos />} />
                {/* Puedes seguir añadiendo más rutas conforme las vayas necesitando */}
            </Routes>

            {/* Aquí el menú de navegación */}
            <Box sx={{ display: 'flex', flexDirection: 'column', mt: 2 }}>
                {navigation.map((item) => (
                    <Box
                        key={item.segment}
                        sx={{ display: 'flex', flexDirection: 'column', cursor: 'pointer' }}
                    >
                        <Box
                            onClick={item.onClick}
                            sx={{
                                display: 'flex',
                                alignItems: 'center',
                                mb: 1,
                                cursor: 'pointer',
                            }}
                        >
                            {item.icon}
                            <Typography sx={{ ml: 1 }}>{item.title}</Typography>
                        </Box>
                        {/* Sub-menus */}
                        {item.children && (
                            <Box sx={{ ml: 2 }}>
                                {item.children.map((subItem) => (
                                    <Box
                                        key={subItem.segment}
                                        onClick={subItem.onClick}
                                        sx={{
                                            display: 'flex',
                                            alignItems: 'center',
                                            mb: 1,
                                            cursor: 'pointer',
                                        }}
                                    >
                                        {subItem.icon}
                                        <Typography sx={{ ml: 1 }}>{subItem.title}</Typography>
                                    </Box>
                                ))}
                            </Box>
                        )}
                    </Box>
                ))}
            </Box>
        </Box>
    );
}
