import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import DashboardLayoutNavigationLinks from './components/DashboardLayoutNavigationLinks';
import { jwtDecode } from 'jwt-decode';

const App = () => {
    const token = localStorage.getItem('token');
    const isAuthenticated = token && jwtDecode(token); // Verifica si hay un token y lo decodifica

    return (
        <Router>
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/dashboard" element={isAuthenticated ? <DashboardLayoutNavigationLinks /> : <Navigate to="/login" />} />
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
