// DashboardLayoutNavigationLinks.js
import React, { useEffect, useState, useCallback } from 'react';
import { createTheme } from '@mui/material/styles';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { Routes, Route, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import Usuario from '../pages/Seguridad/usuario';
import Welcome from './Welcome'; // Componente de bienvenida
import MenuIcon from '@mui/icons-material/Menu'; // Ejemplo de icono

const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

const DashboardLayoutNavigationLinks = () => {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchMenus = useCallback(async () => {
        if (!token) return;

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    }, [token]);

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    const navigation = menuItems.map(item => ({
        segment: item.DisplayName,
        title: item.DisplayName,
        icon: <MenuIcon />, // Usa un icono apropiado
        onClick: () => {
            if (item.MenuURL) navigate(item.MenuURL);
        },
    }));

    return (
        <AppProvider navigation={navigation} theme={demoTheme}>
            <DashboardLayout>
                <Routes>
                    <Route path="/Usuarios" element={<Usuario />} />
                    <Route path="*" element={<Welcome />} />
                </Routes>
            </DashboardLayout>
        </AppProvider>
    );
};

export default DashboardLayoutNavigationLinks;
