import React, { useState, useEffect, useCallback } from 'react';
import { Box, TextField, Button, Grid, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, MenuItem, Select, InputLabel, FormControl, CircularProgress } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const CrearHito = () => {
    const { proyectoId } = useParams(); // Extraer proyectoId de la URL
    const [showAlert, setShowAlert] = useState(null);
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFin, setFechaFin] = useState('');
    const [estado, setEstado] = useState('Pendiente');
    const [proyecto, setProyecto] = useState(null);
    const [hitos, setHitos] = useState([]);
    const [loading, setLoading] = useState(true);  // Agregado para mostrar el estado de carga
    const navigate = useNavigate();

    // Función general para realizar peticiones
    const makeRequest = async (method, url, data = {}) => {
        const token = localStorage.getItem('token');
        if (!token) {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el token de autenticación.',
            });
            return;
        }

        const config = { headers: { 'x-auth-token': token } };
        try {
            const response = await axios[method](url, data, config);
            return response.data;
        } catch (error) {
            console.error('Error:', error);
            setShowAlert({
                severity: 'error',
                message: error.response?.data?.msg || error.message || 'Hubo un error inesperado con la solicitud.',
            });
            return null;
        }
    };

    // Obtener proyecto y hitos
    const fetchProyecto = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        if (data) {
            setProyecto(data);
        } else {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el proyecto.',
            });
        }
    }, [proyectoId]);

    const fetchHitos = useCallback(async () => {
        setLoading(true);  // Marcar como cargando antes de hacer la petición
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/hito/${proyectoId}`);
        setLoading(false);  // Terminar el estado de carga

        if (data && data.hitos) {
            setHitos(data.hitos);
        } else {
            setHitos([]);
        }
    }, [proyectoId]);

    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login');
        } else {
            fetchProyecto();
            fetchHitos();
        }
    }, [navigate, fetchProyecto, fetchHitos]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validación básica de los campos antes de enviar la solicitud
        if (!titulo || !descripcion || !fechaInicio || !fechaFin) {
            setShowAlert({
                severity: 'error',
                message: 'Todos los campos son obligatorios.',
            });
            return;
        }

        if (!proyectoId) {
            setShowAlert({
                severity: 'error',
                message: 'ID del proyecto no encontrado.',
            });
            return;
        }

        const newHito = {
            titulo,
            descripcion,
            fechaInicio,
            fechaFin,
            estado,
            proyectoId
        };

        try {
            const response = await makeRequest('post', 'http://localhost:5000/api/hitos/register', newHito);

            // Si la respuesta contiene un mensaje de éxito
            if (response && response.statusCode === 201) {
                setShowAlert({
                    severity: 'success',
                    message: response.msg,  // Mensaje de éxito
                });

                // Limpiar los campos después de registrar el hito
                setTitulo('');
                setDescripcion('');
                setFechaInicio('');
                setFechaFin('');
                setEstado('Pendiente');
                fetchHitos();
            }
        } catch (error) {
            console.error('Error en la solicitud:', error.response || error);

            // Verificar si el error tiene un mensaje específico en la respuesta
            if (error.response && error.response.data && error.response.data.msg) {
                setShowAlert({
                    severity: 'error',
                    message: error.response.data.msg,  // Mostrar el mensaje de error específico
                });
            } else {
                // Si no hay un mensaje específico, mostrar un mensaje genérico
                setShowAlert({
                    severity: 'error',
                    message: 'Hubo un error inesperado con la solicitud.',
                });
            }
        }
    };

    // Función para ocultar la alerta después de cierto tiempo
    useEffect(() => {
        if (showAlert) {
            const timer = setTimeout(() => {
                setShowAlert(null);  // Limpiar la alerta después de 3 segundos
            }, 1000);
            return () => clearTimeout(timer);  // Limpiar el timer cuando el componente se desmonte o cuando la alerta cambie
        }
    }, [showAlert]);

    return (
        <Box sx={{ padding: 5 }}>
            {showAlert && (
                <AlertMessage
                    severity={showAlert.severity}
                    message={showAlert.message}
                    autoHideDuration={3000}  // 3 segundos de duración
                    sx={{ mb: 4 }}
                />
            )}

            <Typography variant="h5" gutterBottom>
                {proyecto ? `Proyecto: ${proyecto.nombre}` : <CircularProgress size={24} sx={{ mr: 2 }} />}
            </Typography>

            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Título del Hito"
                            value={titulo}
                            onChange={(e) => setTitulo(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Descripción"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de inicio"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaInicio}
                            onChange={(e) => setFechaInicio(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de finalización"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaFin}
                            onChange={(e) => setFechaFin(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel>Estado</InputLabel>
                            <Select
                                value={estado}
                                onChange={(e) => setEstado(e.target.value)}
                                label="Estado"
                            >
                                <MenuItem value="Pendiente">Pendiente</MenuItem>
                                <MenuItem value="En Proceso">En Proceso</MenuItem>
                                <MenuItem value="Finalizado">Finalizado</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            Crear Hito
                        </Button>
                    </Grid>
                </Grid>
            </form>

            {/* Mostrar la tabla de hitos */}
            <Paper sx={{ mt: 4, p: 3 }}>
                <Typography variant="h6" gutterBottom>Hitos del Proyecto</Typography>
                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>ID</TableCell>
                                <TableCell>Título</TableCell>
                                <TableCell>Descripción</TableCell>
                                <TableCell>Fecha de Inicio</TableCell>
                                <TableCell>Fecha de Finalización</TableCell>
                                <TableCell>Estado</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {loading ? (
                                <TableRow>
                                    <TableCell colSpan={6} align="center">
                                        <CircularProgress />
                                    </TableCell>
                                </TableRow>
                            ) : (
                                hitos.map((hito) => (
                                    <TableRow key={hito.id_hito}>
                                        <TableCell>{hito.id_hito}</TableCell>
                                        <TableCell>{hito.nombre_hito}</TableCell>
                                        <TableCell>{hito.descripcion}</TableCell>
                                        <TableCell>{hito.fechaInicio}</TableCell>
                                        <TableCell>{hito.fechaFin}</TableCell>
                                        <TableCell>{hito.estado}</TableCell>
                                    </TableRow>
                                ))
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </Box>
    );
};

export default CrearHito;
