import React, { useState, useEffect } from 'react';
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';

function Usuario() {
    const [usuarios, setUsuarios] = useState([]);
    const navigate = useNavigate();

    // Obtener los usuarios desde la API
    const fetchUsuarios = async () => {
        const response = await fetch('http://localhost:5000/api/auth/users');  // Asegúrate de que esta ruta esté correcta
        const data = await response.json();
        setUsuarios(data);
    };

    useEffect(() => {
        fetchUsuarios();
    }, []);

    // Redirigir al formulario de creación de usuario
    const handleCrearUsuario = () => {
        navigate('/crear-usuario');  // Redirige al formulario de creación de usuario
    };

    return (
        <div>
            <Button 
                variant="contained" 
                color="primary" 
                onClick={handleCrearUsuario} 
                style={{ marginBottom: '20px' }}
            >
                Crear Usuario
            </Button>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Nombre</TableCell>
                            <TableCell>Apellido</TableCell>
                            <TableCell>Usuario</TableCell>
                            <TableCell>Estado</TableCell>
                            <TableCell>Fecha Creación</TableCell>
                            <TableCell>Acciones</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {usuarios.map((usuario) => (
                            <TableRow key={usuario.id}>
                                <TableCell>{usuario.u_nombre}</TableCell>
                                <TableCell>{usuario.u_apellido}</TableCell>
                                <TableCell>{usuario.u_usuario}</TableCell>
                                <TableCell>{usuario.u_estado === 1 ? 'Activo' : 'Inactivo'}</TableCell>
                                <TableCell>{new Date(usuario.u_fechacreacion).toLocaleDateString()}</TableCell>
                                <TableCell>
                                    {/* Aquí podrías agregar botones de editar o eliminar */}
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );
}

export default Usuario;
