import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios'; // Asegúrate de que esto esté aquí
import { jwtDecode } from 'jwt-decode'; // Cambia esto

const demoTheme = createTheme({
  // Aquí puedes definir el tema claro
  palette: {
    mode: 'light', // Establece el modo en 'light' para que sea claro
  },
  // Otras configuraciones del tema
});

function DemoPageContent({ pathname }) {
  return (
    <Box
      sx={{
        py: 4,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <Typography>Dashboard content for {pathname}</Typography>
    </Box>
  );
}

export default function DashboardLayoutNavigationLinks() {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;

  // Aquí puedes agregar la lógica para obtener los menús, por ejemplo:
  // const [menuItems, setMenuItems] = React.useState([]);

  // const fetchMenus = async () => {
  //   const token = localStorage.getItem('token'); // o donde guardes tu token
  //   const decoded = jwtDecode(token);
  //   const username = decoded.user.username; // Asegúrate de que el campo sea correcto

  //   try {
  //     const response = await axios.get(`/api/menus/${username}`);
  //     setMenuItems(response.data);
  //   } catch (error) {
  //     console.error('Error fetching menus:', error);
  //   }
  // };

  // React.useEffect(() => {
  //   fetchMenus();
  // }, []);

  return (
    <AppProvider
      navigation={[
        {
          segment: 'home',
          title: 'Home',
          icon: <DescriptionIcon />,
        },
        {
          segment: 'about',
          title: 'About Us',
          icon: <DescriptionIcon />,
        },
        // Puedes mapear los menús aquí si los has obtenido
        // ...menuItems.map(item => ({
        //   segment: item.segment,
        //   title: item.title,
        //   icon: <YourIconComponent />, // Cambia esto según lo que necesites
        // })),
      ]}
      router={{
        pathname,
        push: (path) => navigate(path),
      }}
      theme={demoTheme} // Pasa el tema claro aquí
    >
      <DashboardLayout>
        <DemoPageContent pathname={pathname} />
      </DashboardLayout>
    </AppProvider>
  );
}
