import React, { useState, useEffect, useRef } from 'react';
import { Button, TextField, Table, TableBody, TableCell, TableContainer, TableFooter, TablePagination, TableRow, TableHead, Paper, Box, Grid, IconButton } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '@mui/material/styles';
import FirstPageIcon from '@mui/icons-material/FirstPage';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import LastPageIcon from '@mui/icons-material/LastPage';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import ConfirmDeleteDialog from '../../components/ConfirmDeleteDialog';  // Asegúrate de importar el componente de diálogo

function TablePaginationActions(props) {
  const theme = useTheme();
  const { count, page, rowsPerPage, onPageChange } = props;

  const handleFirstPageButtonClick = (event) => {
    onPageChange(event, 0);
  };

  const handleBackButtonClick = (event) => {
    onPageChange(event, page - 1);
  };

  const handleNextButtonClick = (event) => {
    onPageChange(event, page + 1);
  };

  const handleLastPageButtonClick = (event) => {
    onPageChange(event, Math.max(0, Math.ceil(count / rowsPerPage) - 1));
  };

  return (
    <Box sx={{ flexShrink: 0, ml: 2.5 }}>
      <IconButton
        onClick={handleFirstPageButtonClick}
        disabled={page === 0}
        aria-label="first page"
      >
        {theme.direction === 'rtl' ? <LastPageIcon /> : <FirstPageIcon />}
      </IconButton>
      <IconButton
        onClick={handleBackButtonClick}
        disabled={page === 0}
        aria-label="previous page"
      >
        {theme.direction === 'rtl' ? <KeyboardArrowRight /> : <KeyboardArrowLeft />}
      </IconButton>
      <IconButton
        onClick={handleNextButtonClick}
        disabled={page >= Math.ceil(count / rowsPerPage) - 1}
        aria-label="next page"
      >
        {theme.direction === 'rtl' ? <KeyboardArrowLeft /> : <KeyboardArrowRight />}
      </IconButton>
      <IconButton
        onClick={handleLastPageButtonClick}
        disabled={page >= Math.ceil(count / rowsPerPage) - 1}
        aria-label="last page"
      >
        {theme.direction === 'rtl' ? <FirstPageIcon /> : <LastPageIcon />}
      </IconButton>
    </Box>
  );
}

const Tareas = () => {
  const [openDialog, setOpenDialog] = useState(false); // Controla el estado del diálogo
  const [selectedTaskId, setSelectedTaskId] = useState(null); // ID de la tarea seleccionada
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [tareas, setTareas] = useState([]);  // Cambio de 'roles' a 'tareas'
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const navigate = useNavigate();

  // Temporizador para limpiar la alerta después de 3 segundos
  const alertTimeout = useRef(null);

  // Función para abrir el diálogo
  const handleOpenDialog = (taskId) => {
    setSelectedTaskId(taskId);
    setOpenDialog(true);
  };

  // Función para cerrar el diálogo
  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedTaskId(null);
  };

  const fetchTareas = async (searchTerm = '') => {
    try {
      const response = await fetch(`http://localhost:5000/api/tareas/tarea?search=${searchTerm}`);
      
      // Check if the response status is OK (2xx range)
      if (!response.ok) {
        throw new Error('Failed to fetch tasks. Server returned ' + response.status);
      }
  
      // Check if the response is in JSON format
      const contentType = response.headers.get('Content-Type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('Server response is not JSON. Content-Type: ' + contentType);
      }
  
      const data = await response.json();  // Now you can safely parse the JSON
      setTareas(data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setShowAlert({ severity: 'error', message: 'Error al obtener las tareas' });
    }
  };
  

  useEffect(() => {
    fetchTareas(search);
  }, [search]);

  // Handle page change
  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  // Handle rows per page change
  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Handle task creation
  const handleCrearTarea = () => {
    navigate('/seguridad/crear-tareas');  // Redireccion a la página de crear tarea
  };

  // Handle task delete
  const handleEliminarTarea = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/tareas/tareadelete/${selectedTaskId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setShowAlert({ severity: 'success', message: 'Tarea eliminada exitosamente' });
        fetchTareas(search); // Recargar tareas con el término de búsqueda actual
      } else {
        setShowAlert({ severity: 'error', message: 'Hubo un error al eliminar la tarea' });
      }
    } catch (error) {
      console.error('Error al eliminar la tarea:', error);
      setShowAlert({ severity: 'error', message: 'Hubo un error al eliminar la tarea' });
    }
  };
  
  useEffect(() => {
    fetchTareas(search); // Asegúrate de pasar siempre el término de búsqueda
  }, [search]);
  

  // Handle task update
  const handleActualizarTarea = (id) => {
    navigate(`/seguridad/editar-tareas/${id}`); // Redireccion para actualizar una tarea
  };


  // Set up the timeout to clear the alert after 3 seconds
  useEffect(() => {
    if (showAlert) {
      // Clear the previous timeout if any
      if (alertTimeout.current) {
        clearTimeout(alertTimeout.current);
      }

      // Set the new timeout to clear the alert
      alertTimeout.current = setTimeout(() => {
        setShowAlert(null); // Hide the alert after 3 seconds
      }, 3000);
    }

    // Cleanup the timeout when the component unmounts
    return () => {
      if (alertTimeout.current) {
        clearTimeout(alertTimeout.current);
      }
    };
  }, [showAlert]);

  const emptyRows =
    page > 0 ? Math.max(0, (1 + page) * rowsPerPage - tareas.length) : 0;

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', padding: 2 }}>

      {/* Mostrar la alerta si hay algún mensaje */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={1000} // La alerta se cierra después de 3 segundos
          sx={{ mb: 4 }} // Agregar margen inferior a la alerta
        />
      )}

      <Grid container spacing={2} alignItems="center" sx={{ marginBottom: '50px', width: '100%' }}>
        <Grid item>
          <TextField
            label="Buscar Tarea"
            variant="outlined"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            sx={{ width: 300 }}
          />
        </Grid>

        <Grid item sx={{ marginLeft: 'auto' }}>
          <Button
            variant="contained"
            color="primary"
            onClick={handleCrearTarea}
          >
            Crear Tarea
          </Button>
        </Grid>
      </Grid>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 500 }} aria-label="custom pagination table">
          <TableHead>
            <TableRow>
              <TableCell>Nombre</TableCell>
              <TableCell align="left">Descripción</TableCell>
              <TableCell align="left">Fecha Inicio</TableCell>
              <TableCell align="left">Fecha de Finalización</TableCell>
              <TableCell align="left">Estado</TableCell>
              <TableCell align="left">Presupuesto</TableCell>
              <TableCell align="left">Responsable</TableCell>
              <TableCell align="left">Fecha de creación</TableCell>
              <TableCell align="left">Uttima modificación</TableCell>
              <TableCell align="center">Acciones</TableCell>
            </TableRow>
          </TableHead>

          <TableBody>
            {(rowsPerPage > 0
              ? tareas.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              : tareas
            ).map((tarea) => (
              <TableRow key={tarea.proyecto_id}>
                <TableCell>{tarea.nombre}</TableCell>
                <TableCell>{tarea.descripcion}</TableCell>
                <TableCell> {new Date(tarea.fecha_inicio).toLocaleDateString()}</TableCell>
                <TableCell>{new Date(tarea.fecha_finalizacion).toLocaleDateString()}</TableCell>
                <TableCell>{tarea.estado}</TableCell>
                <TableCell>{tarea.presupuesto}</TableCell>
                <TableCell>{tarea.u_usuario}</TableCell>
                <TableCell> {new Date(tarea.fecha_creacion).toLocaleDateString()}</TableCell>
                <TableCell>{new Date(tarea.fecha_actualizacion).toLocaleDateString()}</TableCell>
                <TableCell align="center">
                  <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    <IconButton
                      onClick={() => handleActualizarTarea(tarea.TAREA_ID)}
                      color="primary"
                      aria-label="editar"
                      sx={{ marginRight: 1 }}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      onClick={() => handleOpenDialog(tarea.TAREA_ID)}
                      color="secondary"
                      aria-label="eliminar"
                      sx={{ marginLeft: 1 }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </Box>
                </TableCell>
              </TableRow>
            ))}

            {emptyRows > 0 && (
              <TableRow style={{ height: 53 * emptyRows }}>
                <TableCell colSpan={6} />
              </TableRow>
            )}
          </TableBody>

          <TableFooter>
            <TableRow>
              <TablePagination
                rowsPerPageOptions={[5, 10, 25]}
                colSpan={6}
                count={tareas.length}
                rowsPerPage={rowsPerPage}
                page={page}
                SelectProps={{
                  inputProps: { 'aria-label': 'Filas por página' },
                  native: true,
                }}
                onPageChange={handlePageChange}
                onRowsPerPageChange={handleRowsPerPageChange}
                ActionsComponent={TablePaginationActions}
              />
            </TableRow>
          </TableFooter>
        </Table>
      </TableContainer>

      {/* Confirmación de eliminación */}
      <ConfirmDeleteDialog
        open={openDialog}
        onClose={handleCloseDialog}
        onConfirm={handleEliminarTarea}
      />
    </Box>
  );
};

export default Tareas;
