import React, { useEffect, useState, useCallback } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import Usuario from '../pages/Seguridad/usuario';
import { jwtDecode } from 'jwt-decode';

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchMenus = useCallback(async () => {
        if (!isAuthenticated) return;

        try {
            const decoded = jwtDecode(token);
            const username = decoded?.user?.username;

            if (!username) {
                navigate('/login');
                return;
            }

            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
            navigate('/login');
        }
    }, [isAuthenticated, token, navigate]);

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    const handleMenuClick = (url) => {
        if (url) {
            navigate(url);
        }
    };

    return (
        <AppProvider navigation={navigation} theme={demoTheme}>
            <DashboardLayout>
                <Box sx={{ flexGrow: 1 }}>
                    <Routes>
                        {menuItems.map((item) => (
                            <Route
                                key={item.MenuID}
                                path={item.MenuURL}
                                element={item.MenuURL === '/Seguridad/Usuarios' ? <Usuario /> : <Welcome />}
                            />
                        ))}
                        <Route path="*" element={<Welcome />} />
                    </Routes>
                </Box>
            </DashboardLayout>
        </AppProvider>
    );
}

function Welcome() {
    return (
        <Box sx={{ py: 35, display: 'flex', flexDirection: 'column', alignItems: 'center', textAlign: 'center' }}>
            <Typography variant="h4">Bienvenido al Gestor de Control de Calidad!</Typography>
        </Box>
    );
}
