import React, { useState, useEffect } from 'react';
import { TextField, Button, MenuItem, Select, InputLabel, FormControl, Box, Typography, Grid, Snackbar, Alert } from '@mui/material';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta

const ProyectoForm = () => {
    const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
    const [nombre, setNombre] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFinalizacion, setFechaFinalizacion] = useState('');
    const [estado, setEstado] = useState('En progreso');
    const [presupuesto, setPresupuesto] = useState('');
    const [idResponsable, setIdResponsable] = useState('');
    const [responsables, setResponsables] = useState([]);

    // Estados para manejar el Snackbar
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success'); // 'success' o 'error'

    // Realizar la solicitud a la API para obtener los responsables
    useEffect(() => {
        const fetchResponsables = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/responsables');
                if (response.ok) {
                    const data = await response.json();
                    setResponsables(data); // Asumiendo que la respuesta es un array de responsables
                } else {
                    setSnackbarMessage('Error al cargar los responsables');
                    setSnackbarSeverity('error');
                    setOpenSnackbar(true);
                }
            } catch (error) {
                setSnackbarMessage('Hubo un error al cargar los responsables');
                setSnackbarSeverity('error');
                setOpenSnackbar(true);
            }
        };

        fetchResponsables();
    }, []); // Solo se ejecuta una vez cuando el componente se monta

    // Función para manejar el envío del formulario
    const handleSubmit = async (e) => {
        e.preventDefault();

        const proyectoData = {
            nombre,
            descripcion,
            fecha_inicio: fechaInicio,
            fecha_finalizacion: fechaFinalizacion,
            estado,
            presupuesto,
            id_responsable: idResponsable
        };

        try {
            const response = await fetch('http://localhost:5000/api/proyectos', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(proyectoData),
            });

            if (response.ok) {
                setSnackbarMessage('Proyecto insertado correctamente');
                setSnackbarSeverity('success');
                // Limpiar el formulario después de la inserción
                setNombre('');
                setDescripcion('');
                setFechaInicio('');
                setFechaFinalizacion('');
                setEstado('En progreso');
                setPresupuesto('');
                setIdResponsable('');
            } else {
                setSnackbarMessage('Error al insertar el proyecto');
                setSnackbarSeverity('error');
            }
        } catch (error) {
            setSnackbarMessage('Hubo un error al procesar tu solicitud');
            setSnackbarSeverity('error');
        }

        setOpenSnackbar(true);
    };

    // Cerrar el Snackbar
    const handleCloseSnackbar = () => {
        setOpenSnackbar(false);
    };

    return (
        <Box sx={{ maxWidth: 600, margin: '0 auto', padding: 2 }}>
            <Typography variant="h5" align="center" gutterBottom>
                Crear Proyecto
            </Typography>
            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    {/* Campo para Nombre del Proyecto */}
                    <Grid item xs={12}>
                        <TextField
                            label="Nombre del Proyecto"
                            variant="outlined"
                            fullWidth
                            margin="normal"
                            value={nombre}
                            onChange={(e) => setNombre(e.target.value)}
                            required
                        />
                    </Grid>

                    {/* Campo para Descripción */}
                    <Grid item xs={12}>
                        <TextField
                            label="Descripción"
                            variant="outlined"
                            fullWidth
                            margin="normal"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            required
                            multiline
                            rows={4}
                        />
                    </Grid>

                    {/* Campo para Fecha de Inicio */}
                    <Grid item xs={12} sm={6}>
                        <TextField
                            label="Fecha de Inicio"
                            variant="outlined"
                            fullWidth
                            margin="normal"
                            type="date"
                            value={fechaInicio}
                            onChange={(e) => setFechaInicio(e.target.value)}
                            required
                            InputLabelProps={{
                                shrink: true,
                            }}
                        />
                    </Grid>

                    {/* Campo para Fecha de Finalización */}
                    <Grid item xs={12} sm={6}>
                        <TextField
                            label="Fecha de Finalización"
                            variant="outlined"
                            fullWidth
                            margin="normal"
                            type="date"
                            value={fechaFinalizacion}
                            onChange={(e) => setFechaFinalizacion(e.target.value)}
                            required
                            InputLabelProps={{
                                shrink: true,
                            }}
                        />
                    </Grid>

                    {/* Campo para Estado */}
                    <Grid item xs={12}>
                        <FormControl fullWidth margin="normal" required>
                            <InputLabel>Estado</InputLabel>
                            <Select
                                value={estado}
                                onChange={(e) => setEstado(e.target.value)}
                                label="Estado"
                            >
                                <MenuItem value="En progreso">En progreso</MenuItem>
                                <MenuItem value="Finalizado">Finalizado</MenuItem>
                                <MenuItem value="Suspendido">Suspendido</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>

                    {/* Campo para Presupuesto */}
                    <Grid item xs={12} sm={6}>
                        <TextField
                            label="Presupuesto"
                            variant="outlined"
                            fullWidth
                            margin="normal"
                            type="number"
                            step="0.01"
                            value={presupuesto}
                            onChange={(e) => setPresupuesto(e.target.value)}
                        />
                    </Grid>

                    {/* Campo para Responsable (ID) */}
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth margin="normal" required>
                            <InputLabel>Responsable</InputLabel>
                            <Select
                                value={idResponsable}
                                onChange={(e) => setIdResponsable(e.target.value)}
                                label="Responsable"
                            >
                                {responsables.map((responsable) => (
                                    <MenuItem key={responsable.id} value={responsable.id}>
                                        {responsable.nombre}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </Grid>

                    {/* Botón para enviar el formulario */}
                    <Grid item xs={12}>
                        <Button
                            type="submit"
                            variant="contained"
                            color="primary"
                            fullWidth
                            sx={{ marginTop: 2 }}
                        >
                            Insertar Proyecto
                        </Button>
                    </Grid>
                </Grid>
            </form>

            {/* Snackbar para mostrar alertas */}
            <Snackbar 
                open={openSnackbar} 
                autoHideDuration={6000} 
                onClose={handleCloseSnackbar}
            >
                <Alert 
                    onClose={handleCloseSnackbar} 
                    severity={snackbarSeverity} 
                    sx={{ width: '100%' }}
                >
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default ProyectoForm;
