import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { jwtDecode } from 'jwt-decode';

ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardChartsEstados = () => {
    const [proyectos, setProyectos] = useState({
        labels: [],
        datasets: [],
        vencidos: 0,
        noVencidos: 0,
        vencidosNombres: [],
        noVencidosNombres: []
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        try {
            const decoded = jwtDecode(token);
            const id = decoded.user?.id;

            const config = {
                headers: {
                    'x-auth-token': token,
                },
            };

            const response = await axios.get(
                `http://localhost:5000/api/grafica/EstadoProyectos/${id}`,
                config
            );

            const { labels, vencidos, noVencidos, vencidosNombres, noVencidosNombres } = response.data;

            if (!labels || !Array.isArray(labels) || !vencidos || !noVencidos || !vencidosNombres || !noVencidosNombres) {
                setError('Datos incompletos recibidos de la API.');
                return;
            }

            setProyectos({
                labels,
                datasets: [
                    {
                        data: [vencidos, noVencidos],
                        backgroundColor: ['#FF6384', '#36A2EB'],
                    },
                ],
                vencidos,
                noVencidos,
                vencidosNombres,
                noVencidosNombres,
            });
        } catch (err) {
            console.error('Error al obtener los proyectos:', err);
            const errorMsg = err.response?.data?.msg || 'Error desconocido';
            setError(`No se pudieron cargar los datos de los proyectos: ${errorMsg}`);
        } finally {
            setLoading(false);
        }
    }, [isAuthenticated, token]);

    useEffect(() => {
        if (!isAuthenticated) return;
        fetchProyectos();
    }, [isAuthenticated, fetchProyectos]);

    if (loading) return <div>Cargando datos...</div>;

    if (error) {
        return <div>{error}</div>;
    }

    if (!proyectos.labels.length || !proyectos.datasets.length) {
        return <div>No se encontraron proyectos asignados.</div>;
    }

    return (
        <div style={{ width: '100%', height: '100%' }}>
            <h2 style={{ textAlign: 'center' }}>Proyectos</h2>
            <div style={{ height: '100%' }}>
                <Pie
                    data={{
                        labels: proyectos.labels,
                        datasets: proyectos.datasets,
                    }}
                    options={{
                        responsive: true,
                        plugins: {
                            legend: {
                                position: 'top',
                            },
                            tooltip: {
                                callbacks: {
                                    label: (tooltipItem) => {
                                        const index = tooltipItem.dataIndex;
                                        const projectNames = index === 0
                                            ? proyectos.vencidosNombres
                                            : proyectos.noVencidosNombres;
                                        return `${tooltipItem.raw} Proyectos: ${projectNames.join(', ')}`;
                                    },
                                },
                            },
                        },
                    }}
                />
            </div>
        </div>
    );
};

export default DashboardChartsEstados;
