// CrearUsuario.js
import React, { useState } from 'react';
import { Box, Button, TextField, Typography } from '@mui/material';
import DashboardPageLayout from '../../components/DashboardPageLayout';

const CrearUsuario = ({ navigation }) => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log("Usuario creado:", { username, email, password });
    };

    return (
        <DashboardPageLayout navigation={navigation}>
            <Box
                component="form"
                onSubmit={handleSubmit}
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    width: '100%', // Para ocupar el 100% del contenedor
                    maxWidth: 400, // Ancho m치ximo del formulario
                    margin: '0 auto', // Centrado horizontalmente
                    padding: 2,
                }}
            >
                <Typography variant="h5" align="center" gutterBottom>
                    Crear Usuario
                </Typography>
                <TextField
                    label="Nombre de usuario"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    margin="normal"
                    fullWidth
                />
                <TextField
                    label="Correo electr칩nico"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    margin="normal"
                    fullWidth
                />
                <TextField
                    label="Contrase침a"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    margin="normal"
                    fullWidth
                />
                <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>
                    Crear Usuario
                </Button>
            </Box>
        </DashboardPageLayout>
    );
};

export default CrearUsuario;
