import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const CrearHito = () => {
  const [showAlert, setShowAlert] = useState(null);
  const navigate = useNavigate();

  // Estados para los campos del formulario
  const [titulo, setTitulo] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fecha, setFecha] = useState('');
  const [proyectos, setProyectos] = useState([]); // Lista de proyectos para la tabla
  const [proyectoId, setProyectoId] = useState(''); // Para seleccionar el proyecto

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    } else {
      fetchProyectos(); // Cargar la lista de proyectos
    }
  }, [navigate]);

  const fetchProyectos = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get('http://localhost:5000/api/proyectos', config); // Ajusta la URL de acuerdo a tu API

      setProyectos(response.data); // Guardar los proyectos en el estado
    } catch (error) {
      console.error('Error al cargar los proyectos:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los proyectos.',
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!titulo || !descripcion || !fecha || !proyectoId) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const newHito = { titulo, descripcion, fecha, proyectoId };

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.post('http://localhost:5000/api/hitos', newHito, config);

      if (response.data.msg === 'Hito creado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg,
        });

        setTitulo('');
        setDescripcion('');
        setFecha('');
        setProyectoId('');

        setTimeout(() => {
          navigate('/proyectos/hitos');
        }, 2000);
      } else {
        setShowAlert({
          severity: 'error',
          message: response.data.msg || 'Error al crear el hito.',
        });
      }
    } catch (error) {
      console.error('Error al crear el hito:', error.response || error);

      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el hito. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom>
        Crear Nuevo Hito
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Título del Hito"
              value={titulo}
              onChange={(e) => setTitulo(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha"
              type="date"
              InputLabelProps={{ shrink: true }}
              value={fecha}
              onChange={(e) => setFecha(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              select
              fullWidth
              label="Selecciona el Proyecto"
              value={proyectoId}
              onChange={(e) => setProyectoId(e.target.value)}
              required
              SelectProps={{
                native: true,
              }}
            >
              <option value="">Seleccionar Proyecto</option>
              {proyectos.map((proyecto) => (
                <option key={proyecto.id} value={proyecto.id}>
                  {proyecto.nombre}
                </option>
              ))}
            </TextField>
          </Grid>
          <Grid item xs={6}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Crear Hito
            </Button>
          </Grid>
        </Grid>
      </form>

      <Typography variant="h5" sx={{ mt: 4, mb: 2 }}>
        Lista de Proyectos
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID Proyecto</TableCell>
              <TableCell>Nombre del Proyecto</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {proyectos.map((proyecto) => (
              <TableRow key={proyecto.id}>
                <TableCell>{proyecto.id}</TableCell>
                <TableCell>{proyecto.nombre}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default CrearHito;
