import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend
} from 'chart.js';
import { jwtDecode } from 'jwt-decode';

ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardChartsEstados = () => {
    const [proyectos, setProyectos] = useState({
        labels: ["En progreso", "Finalizado", "Suspendido"],
        datasets: [],
        estadoProyectos: {
            enProgreso: 0,
            finalizados: 0,
            suspendidos: 0
        }
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        try {
            const decoded = jwtDecode(token);
            const id = decoded.user?.id; // Verifica que `user` exista en el token.

            const config = {
                headers: {
                    'x-auth-token': token, 
                },
            };

            const response = await axios.get(
                `http://localhost:5000/api/grafica/EstadoProyectos/${id}`,
                config
            );

            // Verifica la respuesta de la API
            console.log('Datos recibidos de la API:', response.data);

            const { estadoProyectos } = response.data;

            if (!estadoProyectos) {
                setError('Datos incompletos recibidos de la API.');
                return;
            }

            setProyectos({
                labels: ["En progreso", "Finalizado", "Suspendido"],
                datasets: [
                    {
                        label: "Cantidad de Proyectos",
                        data: [
                            estadoProyectos.enProgreso,
                            estadoProyectos.finalizados,
                            estadoProyectos.suspendidos
                        ],
                        backgroundColor: ["blue", "green", "orange"], // Colores para los proyectos
                    }
                ],
                estadoProyectos,
            });
        } catch (err) {
            console.error('Error al obtener los proyectos:', err);
            const errorMsg = err.response?.data?.msg || 'Error desconocido';
            setError(`No se pudieron cargar los datos de los proyectos: ${errorMsg}`);
        } finally {
            setLoading(false);
        }
    }, [isAuthenticated, token]);

    useEffect(() => {
        if (!isAuthenticated) return;
        fetchProyectos();
    }, [isAuthenticated, fetchProyectos]);

    if (loading) return <div>Cargando datos...</div>;

    if (error) {
        return <div>{error}</div>;
    }

    // Verifica si los datos están correctamente estructurados antes de renderizar
    if (!proyectos.datasets || proyectos.datasets.length === 0) {
        return <div>No se encontraron proyectos asignados.</div>;
    }

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Estado de los Proyectos</h2>
            <Pie
                data={{
                    labels: proyectos.labels,
                    datasets: proyectos.datasets,
                }}
                options={{
                    responsive: true,
                    plugins: {
                        legend: {
                            position: 'top',
                        },
                        tooltip: {
                            callbacks: {
                                label: (tooltipItem) => {
                                    const datasetIndex = tooltipItem.datasetIndex;
                                    const datasetLabel = proyectos.datasets[datasetIndex]?.label || 'Desconocido';
                                    const state = proyectos.labels[tooltipItem.dataIndex];
                                    const value = tooltipItem.raw;
                                    return `${datasetLabel}: ${value} Proyectos (${state})`;
                                },
                            },
                        },
                    },
                }}
            />
        </div>
    );
};

export default DashboardChartsEstados;
