import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom'; // Importar useParams para obtener el id
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Función para verificar si está autenticado

const FormMenu = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el id del menú desde la URL

  // Estados para los campos del formulario
  const [displayName, setDisplayName] = useState('');
  const [parentMenuID, setParentMenuID] = useState('');
  const [orderNumber, setOrderNumber] = useState('');
  const [menuURL, setMenuURL] = useState('');
  const [menuIcon, setMenuIcon] = useState('');

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    } else {
      // Si estamos editando, cargar los datos del menú
      if (id) {
        fetchMenuData(id);
      }
    }
  }, [id, navigate]);

  // Función para cargar los datos del menú
  const fetchMenuData = async (menuId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };

      const response = await axios.get(`http://localhost:5000/api/menu/${menuId}`, config);
      const menuData = response.data;

      // Llenar los campos del formulario con los datos del menú
      setDisplayName(menuData.displayName);
      setParentMenuID(menuData.parentMenuID);
      setOrderNumber(menuData.orderNumber);
      setMenuURL(menuData.menuURL);
      setMenuIcon(menuData.menuIcon);
    } catch (error) {
      console.error('Error al cargar los datos del menú:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los datos del menú.',
      });
    }
  };

  // Función para manejar el envío del formulario (actualizar menú)
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples
    if (!displayName || !parentMenuID || !orderNumber || !menuURL || !menuIcon) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const updatedMenu = { displayName, parentMenuID, orderNumber, menuURL, menuIcon };

    try {
      const token = localStorage.getItem('token');

      // Verificar si el token existe
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };

      // Hacer la solicitud PUT para actualizar el menú
      const response = await axios.put(`http://localhost:5000/api/menu/${id}`, updatedMenu, config);

      // Verificar la respuesta de la API
      if (response.data.msg === 'Menú actualizado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg, // Mostrar el mensaje de éxito
        });

        // Limpiar los campos del formulario si el menú se actualizó con éxito
        setDisplayName('');
        setParentMenuID('');
        setOrderNumber('');
        setMenuURL('');
        setMenuIcon('');

        // Redirigir a la lista de menús después de 2 segundos
        setTimeout(() => {
          navigate('/menus'); // Cambia esta ruta a la ruta que prefieras
        }, 2000);
      } else {
        setShowAlert({
          severity: 'error',
          message: response.data.msg || 'Error al actualizar el menú.',
        });
      }
    } catch (error) {
      console.error('Error al actualizar el menú:', error.response || error);

      // Si hay un error específico de la API
      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al actualizar el menú. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000} // La alerta se cierra después de 3 segundos
          sx={{ mb: 4 }} // Agregar margen inferior a la alerta
        />
      )}

      <Typography variant="h4" gutterBottom>
        Editar Menú
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Campo para Display Name */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Display Name"
              value={displayName}
              onChange={(e) => setDisplayName(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Parent Menu ID */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Parent Menu ID"
              value={parentMenuID}
              onChange={(e) => setParentMenuID(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Order Number */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Order Number"
              type="number"
              value={orderNumber}
              onChange={(e) => setOrderNumber(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Menu URL */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Menu URL"
              value={menuURL}
              onChange={(e) => setMenuURL(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Menu Icon */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Menu Icon"
              value={menuIcon}
              onChange={(e) => setMenuIcon(e.target.value)}
              required
            />
          </Grid>

          {/* Botón para enviar el formulario */}
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Actualizar Menú
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default FormMenu;
