import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import DashboardLayoutNavigationLinks from './components/DashboardLayoutNavigationLinks';
import UsuarioForm from './pages/Seguridad/usuario';

const App = () => {
    const isAuthenticated = Boolean(localStorage.getItem('token')); // Verifica si hay un token
    console.log('Usuario autenticado:', isAuthenticated);

    return (
        <Router>
        <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/dashboard" element={isAuthenticated ? <DashboardLayoutNavigationLinks /> : <Navigate to="/login" />} />
            <Route path="/dashboard/Usuarios" element={isAuthenticated ? <UsuarioForm /> : <Navigate to="/login" />} />
            {/* Redirige cualquier otra ruta al login */}
            <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
    </Router>
    );
};

export default App;
