import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box } from '@mui/material';
import { styled } from '@mui/system';
import { useNavigate } from 'react-router-dom'; // Para redirigir
import logo from '../assets/LogoPrincipal.png'; // Asegúrate de que la ruta sea correcta

// Estilo para el logo circular
const CircularLogo = styled('img')({
  width: '350px', // Ajusta el tamaño según sea necesario
  height: '200px',
  borderRadius: '50%', // Esto hace que el logo sea circular
  display: 'block',
  margin: '0 auto', // Centra el logo
});

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate(); // Hook para redirigir

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Validación básica
    const newErrors = {};
    if (!usuario) newErrors.usuario = 'El usuario es requerido';
    if (!password) newErrors.password = 'La contraseña es requerida';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return; // Detenemos el envío del formulario si hay errores
    }

    try {
      // Consumir la API
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // Supongamos que recibes un token o un estado de éxito
        console.log('Inicio de sesión exitoso', data);

        // Redirigir a la página Home
        navigate('/Home');
      } else {
        // Manejo de errores de la API
        console.error('Error al iniciar sesión:', data.message);
        setErrors({ api: data.message });
      }
    } catch (error) {
      console.error('Error en la conexión:', error);
      setErrors({ api: 'Error en la conexión. Inténtalo más tarde.' });
    }
  };

  return (
    <Container maxWidth="xs" sx={{ mt: 8 }}>
      <CircularLogo src={logo} alt="Logo" />
      <Typography variant="h4" align="center" gutterBottom>
        Iniciar Sesión
      </Typography>
      <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
        <TextField
          required
          fullWidth
          label="Usuario" // "Usuario"
          margin="normal"
          value={usuario}
          onChange={(e) => setUsuario(e.target.value)}
          error={Boolean(errors.usuario)}
          helperText={errors.usuario}
        />
        <TextField
          required
          fullWidth
          label="Contraseña" // "Contraseña"
          type="password"
          margin="normal"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          error={Boolean(errors.password)}
          helperText={errors.password}
        />
        {errors.api && (
          <Typography color="error" variant="body2">
            {errors.api}
          </Typography>
        )}
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Iniciar Sesión
        </Button>
      </Box>
    </Container>
  );
};

export default Login;
