import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import DashboardLayoutNavigationLinks from './components/DashboardPageLayout';
import Login from './pages/Login'; // Asegúrate de tener esta página
import Usuario from './pages/Seguridad/usuario'; // Verifica que la ruta sea correcta

function App() {
    return (
        <Router>
            <Routes>
                {/* Ruta para el login */}
                <Route path="/login" element={<Login />} />

                {/* Rutas dentro de /dashboard/* deben estar gestionadas por DashboardLayoutNavigationLinks */}
                <Route path="/dashboard/*" element={<DashboardLayoutNavigationLinks />} />

                {/* Ruta para Usuarios (fija) */}
                <Route path="/dashboard/seguridad/usuarios" element={<Usuario />} />

                {/* Puedes agregar otras rutas fijas si es necesario */}
                <Route path="/dashboard" element={<Welcome />} />

                {/* Redirigir a login si la ruta no existe */}
                <Route path="*" element={<Login />} />
            </Routes>
        </Router>
    );
}


export default App;
