import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Verifica autenticación

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [nombreProyecto, setNombreProyecto] = useState('');
  const [descripcionHito, setDescripcionHito] = useState(''); // Descripción dinámica
  const [hitos, setHitos] = useState([]); // Lista de hitos
  const [hitoSeleccionado, setHitoSeleccionado] = useState('');
  const [formularioHabilitado, setFormularioHabilitado] = useState(false); // Habilita el formulario secundario
  const navigate = useNavigate();
  const { id } = useParams(); // ID del proyecto desde la URL

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    }
  }, [navigate]);

  useEffect(() => {
    const fetchProyecto = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/proyectos/proyecto/${id}`);
        const proyecto = response.data;

        setNombreProyecto(proyecto.nombre);
        setHitos(proyecto.hitos || []);
      } catch (error) {
        console.error('Error al obtener el proyecto:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los datos del proyecto.',
        });
      }
    };

    if (id) {
      fetchProyecto();
    }
  }, [id]);

  const handleHitoChange = (hitoId) => {
    setHitoSeleccionado(hitoId);

    // Buscar la descripción del hito seleccionado
    const hito = hitos.find((h) => h.id_hito === hitoId);
    setDescripcionHito(hito ? hito.descripcion : '');
  };

  const handleEnableForm = () => {
    setFormularioHabilitado(true);
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom align="center">
        Crear Plan de Prueba
      </Typography>

      <form>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              label="Nombre del Proyecto"
              value={nombreProyecto}
              disabled={true}
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12}>
            <Button fullWidth variant="contained" color="primary" onClick={handleEnableForm}>
              Crear Plan de Prueba
            </Button>
          </Grid>

          {formularioHabilitado && (
            <>
              <Grid item xs={12}>
                <InputLabel>Seleccionar Hito</InputLabel>
                <Select
                  value={hitoSeleccionado || ""}
                  onChange={(e) => handleHitoChange(e.target.value)}
                  fullWidth
                  required
                  displayEmpty
                >
                  <MenuItem value="" disabled>
                    Seleccione un Hito
                  </MenuItem>
                  {hitos && hitos.length > 0 ? (
                    hitos.map((hito) => (
                      <MenuItem key={hito.id_hito} value={hito.id_hito}>
                        {hito.nombre_hito}
                      </MenuItem>
                    ))
                  ) : (
                    <MenuItem value="" disabled>
                      No hay hitos disponibles
                    </MenuItem>
                  )}
                </Select>
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Descripción del Hito"
                  value={descripcionHito}
                  disabled={true} // Campo deshabilitado, cambia automáticamente
                  variant="outlined"
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Resultado Esperado"
                  required
                  variant="outlined"
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Prueba Realizada"
                  required
                  variant="outlined"
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Estado de la Prueba"
                  required
                  variant="outlined"
                />
              </Grid>
            </>
          )}
        </Grid>
      </form>
    </Box>
  );
};

export default CrearPlanDePrueba;
