// DashboardLayoutNavigationLinks.js
import React, { useEffect, useState, useCallback } from 'react';
import { createTheme } from '@mui/material/styles';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import Usuario from '../pages/Seguridad/usuario';
import { jwtDecode } from 'jwt-decode';
import DashboardPageLayout from './DashboardPageLayout';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    //const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchMenus = useCallback(async () => {
        if (!token) return;

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    }, [token]);

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    return (
        <AppProvider theme={demoTheme}>
            <DashboardLayout>
                <Routes>
                    {/* Utiliza DashboardPageLayout para las rutas de contenido */}
                    <Route path="/" element={<DashboardPageLayout />}>
                        <Route index element={<Welcome />} /> {/* Ruta predeterminada de bienvenida */}
                        <Route path="usuarios" element={<Usuario />} />
                        {/* Agrega más rutas según sea necesario */}
                    </Route>
                </Routes>
            </DashboardLayout>
        </AppProvider>
    );
}

function Welcome() {
    return (
        <Box
            sx={{
                py: 35,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                textAlign: 'center',
            }}
        >
            <Typography variant="h4">Bienvenido al Gestor de Control de Calidad!</Typography>
        </Box>
    );
}
