import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel, FormControl, CircularProgress, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, IconButton } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const EditarProyecto = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [responsables, setResponsables] = useState([]); // Estado para los responsables
  const [loadingResponsables, setLoadingResponsables] = useState(true); // Cargando responsables
  const [hitos, setHitos] = useState([]); // Estado para los hitos del proyecto
  const [loadingHitos, setLoadingHitos] = useState(true); // Estado para el loading de hitos

  // Campos del proyecto
  const [nombreProyecto, setNombreProyecto] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [presupuesto, setPresupuesto] = useState('');
  const [estado, setEstado] = useState('');
  const [responsable, setResponsable] = useState('');
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el ID del proyecto desde la URL

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    }
  }, [navigate]);

  // Obtener la lista de responsables desde la API
  useEffect(() => {
    const fetchResponsables = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/proyectos/responsables');
        setResponsables(response.data);
      } catch (error) {
        console.error('Error al obtener responsables:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los responsables.',
        });
      } finally {
        setLoadingResponsables(false);
      }
    };

    fetchResponsables();
  }, []);

  // Obtener los datos del proyecto y sus hitos
  useEffect(() => {
    const fetchProyectoYHitos = async () => {
      try {
        // Obtener el proyecto
        const responseProyecto = await axios.get(`http://localhost:5000/api/proyectos/proyecto/${id}`);
        const proyecto = responseProyecto.data;

        // Formatear fechas
        const formatoFechaInicio = new Date(proyecto.fecha_inicio).toISOString().split('T')[0];
        const formatoFechaFin = new Date(proyecto.fecha_finalizacion).toISOString().split('T')[0];

        setNombreProyecto(proyecto.nombre);
        setDescripcion(proyecto.descripcion);
        setFechaInicio(formatoFechaInicio);
        setFechaFin(formatoFechaFin);
        setPresupuesto(proyecto.presupuesto);
        setEstado(proyecto.estado);
        setResponsable(proyecto.id_responsable);

        // Obtener los hitos asociados
        const responseHitos = await axios.get(`http://localhost:5000/api/proyectos/${id}/hitos`);
        setHitos(responseHitos.data);
      } catch (error) {
        console.error('Error al obtener el proyecto o hitos:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los datos del proyecto o sus hitos.',
        });
      } finally {
        setLoadingHitos(false);
      }
    };

    fetchProyectoYHitos();
  }, [id]);

  const handleDeleteHito = async (hitoId) => {
    try {
      await axios.delete(`http://localhost:5000/api/hitos/${hitoId}`);
      setHitos(hitos.filter((hito) => hito.id !== hitoId));
      setShowAlert({
        severity: 'success',
        message: 'Hito eliminado correctamente.',
      });
    } catch (error) {
      console.error('Error al eliminar el hito:', error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al eliminar el hito.',
      });
    }
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!nombreProyecto || !descripcion || !fechaInicio || !fechaFin || !presupuesto || !estado || !responsable) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const updatedProyecto = { nombreProyecto, descripcion, fechaInicio, fechaFin, presupuesto, estado, responsable };

    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      const response = await axios.put(`http://localhost:5000/api/proyectos/editProyecto/${id}`, updatedProyecto, config);

      if (response.data.msg === 'El proyecto ya existe') {
        setShowAlert({
          severity: 'error',
          message: response.data.msg,
        });
      } else if (response.data.msg === 'Proyecto actualizado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg,
        });

        setTimeout(() => {
          navigate('/gestiones/proyectos');
        }, 2000);
      }
    } catch (error) {
      console.error('Error al actualizar el proyecto:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al actualizar el proyecto. Inténtalo de nuevo.',
      });
    }
  };

  return (
    <Box sx={{ padding: 5, maxWidth: 1200, margin: 'auto' }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom align="center">
        Editar Proyecto
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Campos del formulario */}
          {/* ... Aquí irían los mismos campos que en el código original */}
        </Grid>
        <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 3 }}>
          Actualizar Proyecto
        </Button>
      </form>

      <Typography variant="h5" gutterBottom sx={{ mt: 5 }}>
        Hitos del Proyecto
      </Typography>
      {loadingHitos ? (
        <CircularProgress />
      ) : (
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Nombre</TableCell>
                <TableCell>Descripción</TableCell>
                <TableCell>Fecha de Inicio</TableCell>
                <TableCell>Fecha de Fin</TableCell>
                <TableCell>Acciones</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {hitos.map((hito) => (
                <TableRow key={hito.id}>
                  <TableCell>{hito.nombre}</TableCell>
                  <TableCell>{hito.descripcion}</TableCell>
                  <TableCell>{hito.fecha_inicio}</TableCell>
                  <TableCell>{hito.fecha_fin}</TableCell>
                  <TableCell>
                    <IconButton color="primary" onClick={() => navigate(`/hitos/editar/${hito.id}`)}>
                      <EditIcon />
                    </IconButton>
                    <IconButton color="secondary" onClick={() => handleDeleteHito(hito.id)}>
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </Box>
  );
};

export default EditarProyecto;
