import React, { useEffect, useState } from 'react';
import { Pie, Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend,
    LineElement,
    CategoryScale,
    LinearScale,
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, LineElement, CategoryScale, LinearScale);

const DashboardMetrics = () => {
    const [cobertura, setCobertura] = useState(85); // Cobertura de pruebas
    const [defectos, setDefectos] = useState({ encontrados: 30, corregidos: 25 });
    const [tiempoResolucion, setTiempoResolucion] = useState([5, 4, 3, 6, 2]);

    const dataCobertura = {
        labels: ['Cobertura', 'Sin Cobertura'],
        datasets: [{
            data: [cobertura, 100 - cobertura],
            backgroundColor: ['green', 'red'],
            hoverOffset: 4,
        }],
    };

    const dataDefectos = {
        labels: ['Defectos Encontrados', 'Defectos Corregidos'],
        datasets: [{
            data: [defectos.encontrados, defectos.corregidos],
            backgroundColor: ['red', 'blue'],
            hoverOffset: 4,
        }],
    };

    const dataTiempoResolucion = {
        labels: ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes'],
        datasets: [{
            label: 'Tiempo Promedio de Resolución',
            data: tiempoResolucion,
            fill: false,
            borderColor: 'orange',
            tension: 0.1,
        }],
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2 style={{ textAlign: 'center' }}>Métricas de Calidad y Pruebas</h2>
            <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                {/* Cobertura de Pruebas */}
                <div style={{ width: '30%' }}>
                    <h3 style={{ textAlign: 'center' }}>Cobertura de Pruebas</h3>
                    <Pie data={dataCobertura} options={{ responsive: true }} />
                </div>
                {/* Defectos Encontrados y Corregidos */}
                <div style={{ width: '30%' }}>
                    <h3 style={{ textAlign: 'center' }}>Defectos Encontrados y Corregidos</h3>
                    <Pie data={dataDefectos} options={{ responsive: true }} />
                </div>
                {/* Tiempo Promedio de Resolución */}
                <div style={{ width: '30%' }}>
                    <h3 style={{ textAlign: 'center' }}>Tiempo Promedio de Resolución</h3>
                    <Line data={dataTiempoResolucion} options={{ responsive: true }} />
                </div>
            </div>
        </div>
    );
};

export default DashboardMetrics;
