import React, { useState, useEffect } from 'react';
import { TextField, Button, MenuItem, FormControl, InputLabel, Select, Grid, Typography } from '@mui/material';
import { useParams } from 'react-router-dom'; // Usamos useParams para obtener el ID de la URL
import axios from 'axios'; // Puedes usar axios o fetch para hacer la solicitud a la API

const FormMenu = () => {
  const { userId } = useParams(); // Obtén el ID desde la URL
  const [formData, setFormData] = useState({
    displayName: '',
    parentMenuID: '',
    orderNumber: '',
    menuURL: '',
    menuIcon: '',
  });

  const [historyData, setHistoryData] = useState(null); // Para almacenar los datos históricos
  const [loading, setLoading] = useState(true); // Para manejar el estado de carga

  // Función para manejar el cambio de los inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Aquí puedes enviar los datos actualizados a tu API o manejarlos como sea necesario
      await axios.put(`/api/menu/${id}`, formData);
      console.log(formData);
      // Mostrar un mensaje de éxito
    } catch (err) {
      console.error('Error al actualizar los datos', err);
    }
  };

  // Recuperar los datos de la API usando el ID
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Recuperar los datos del usuario con el ID de la URL
        const response = await axios.get(`http://localhost:5000/api/auth/usersedit/${userId}`);
        setFormData(response.data); // Asumimos que los datos vienen en la respuesta
        setHistoryData(response.data); // Para mostrar los datos como historial
        setLoading(false);
      } catch (err) {
        console.error('Error al obtener los datos del menú:', err);
      }
    };
    
    fetchData();
  }, [id]); // La dependencia de `id` asegura que se recarguen los datos cuando el ID cambie

  if (loading) {
    return <Typography>Cargando...</Typography>;
  }

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <Typography variant="h4" gutterBottom>
        Formulario de Menú
      </Typography>

      {/* Mostrar los datos históricos del usuario */}
      <div style={{ marginBottom: '20px' }}>
        <Typography variant="h6" gutterBottom>
          Historial de Datos:
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <Typography><strong>Nombre:</strong> {historyData.u_nombre}</Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography><strong>Apellido:</strong> {historyData.u_apellido}</Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography><strong>Usuario:</strong> {historyData.u_usuario}</Typography>
          </Grid>
        </Grid>
      </div>

      {/* Formulario de edición */}
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          {/* Display Name */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Display Name"
              name="displayName"
              value={formData.displayName}
              onChange={handleChange}
              required
            />
          </Grid>

          {/* Parent Menu ID */}
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>Parent Menu ID</InputLabel>
              <Select
                label="Parent Menu ID"
                name="parentMenuID"
                value={formData.parentMenuID}
                onChange={handleChange}
                required
              >
                <MenuItem value={1}>Menú 1</MenuItem>
                <MenuItem value={2}>Menú 2</MenuItem>
                <MenuItem value={3}>Menú 3</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          {/* Order Number */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Order Number"
              name="orderNumber"
              type="number"
              value={formData.orderNumber}
              onChange={handleChange}
              required
            />
          </Grid>

          {/* Menu URL */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Menu URL"
              name="menuURL"
              value={formData.menuURL}
              onChange={handleChange}
              required
            />
          </Grid>

          {/* Menu Icon */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Menu Icon"
              name="menuIcon"
              value={formData.menuIcon}
              onChange={handleChange}
              required
            />
          </Grid>

          {/* Submit Button */}
          <Grid item xs={12}>
            <Button variant="contained" color="primary" type="submit" fullWidth>
              Enviar
            </Button>
          </Grid>
        </Grid>
      </form>
    </div>
  );
};

export default FormMenu;
