import React, { useState, useEffect } from 'react';
import { Button, TextField, Table, TableBody, TableCell, TableContainer, TableFooter, TablePagination, TableRow, TableHead, Paper, Box, Grid, IconButton, Switch } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '@mui/material/styles';
import FirstPageIcon from '@mui/icons-material/FirstPage';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import LastPageIcon from '@mui/icons-material/LastPage';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta


function TablePaginationActions(props) {
  const theme = useTheme();
  const { count, page, rowsPerPage, onPageChange } = props;

  const handleFirstPageButtonClick = (event) => {
    onPageChange(event, 0);
  };

  const handleBackButtonClick = (event) => {
    onPageChange(event, page - 1);
  };

  const handleNextButtonClick = (event) => {
    onPageChange(event, page + 1);
  };

  const handleLastPageButtonClick = (event) => {
    onPageChange(event, Math.max(0, Math.ceil(count / rowsPerPage) - 1));
  };

  return (
    <Box sx={{ flexShrink: 0, ml: 2.5 }}>
      <IconButton
        onClick={handleFirstPageButtonClick}
        disabled={page === 0}
        aria-label="first page"
      >
        {theme.direction === 'rtl' ? <LastPageIcon /> : <FirstPageIcon />}
      </IconButton>
      <IconButton
        onClick={handleBackButtonClick}
        disabled={page === 0}
        aria-label="previous page"
      >
        {theme.direction === 'rtl' ? <KeyboardArrowRight /> : <KeyboardArrowLeft />}
      </IconButton>
      <IconButton
        onClick={handleNextButtonClick}
        disabled={page >= Math.ceil(count / rowsPerPage) - 1}
        aria-label="next page"
      >
        {theme.direction === 'rtl' ? <KeyboardArrowLeft /> : <KeyboardArrowRight />}
      </IconButton>
      <IconButton
        onClick={handleLastPageButtonClick}
        disabled={page >= Math.ceil(count / rowsPerPage) - 1}
        aria-label="last page"
      >
        {theme.direction === 'rtl' ? <FirstPageIcon /> : <LastPageIcon />}
      </IconButton>
    </Box>
  );
}

const Usuario = () => {
  const [usuarios, setUsuarios] = useState([]);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const navigate = useNavigate();

  // Fetch users from the server
  const fetchUsuarios = async (searchTerm = '') => {
    const response = await fetch(`http://localhost:5000/api/auth/users?search=${searchTerm}`);
    const data = await response.json();
    setUsuarios(data);
  };

  useEffect(() => {
    fetchUsuarios(search);
  }, [search]);

  // Handle page change
  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  // Handle rows per page change
  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Handle user creation
  const handleCrearUsuario = () => {
    navigate('/seguridad/crear-usuario');  // Redirect to the user creation page
  };

  // Handle user delete
  const handleEliminarUsuario = async (id) => {
    const confirmDelete = window.confirm('¿Estás seguro de que quieres eliminar este usuario?');
    if (confirmDelete) {
      try {
        const response = await fetch(`http://localhost:5000/api/auth/users/${id}`, {
          method: 'DELETE',
        });
        if (response.ok) {
          alert('Usuario eliminado exitosamente');
          fetchUsuarios(search); // Reload the user list
        } else {
          alert('Hubo un error al eliminar el usuario');
        }
      } catch (error) {
        console.error('Error al eliminar el usuario:', error);
        alert('Hubo un error al eliminar el usuario');
      }
    }
  };

  // Handle user update
  const handleActualizarUsuario = (id) => {
    navigate(`/seguridad/actualizar-usuario/${id}`); // Redirect to the update page
  };

  // Toggle user status
  const handleToggleEstado = async (id, currentEstado) => {
    const newEstado = currentEstado === 1 ? 0 : 1; // Toggle between 1 (active) and 0 (inactive)

    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/deactivate/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ u_estado: newEstado })
      });

      if (response.ok) {
        {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={3000} // La alerta se cierra después de 5 segundos
          sx={{ mb: 4 }} // Agregar margen inferior a la alerta
        />
      )}
        fetchUsuarios(search); // Reload the user list
      } else {
        alert('Hubo un error al actualizar el estado');
      }
    } catch (error) {
      console.error('Error al actualizar el estado:', error);
      alert('Hubo un error al actualizar el estado');
    }
  };

  const emptyRows =
    page > 0 ? Math.max(0, (1 + page) * rowsPerPage - usuarios.length) : 0;

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', padding: 2 }}>
      <Grid container spacing={2} alignItems="center" sx={{ marginBottom: '50px', width: '100%' }}>
        <Grid item>
          <TextField
            label="Buscar Usuario"
            variant="outlined"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            sx={{ width: 300 }}
          />
        </Grid>

        <Grid item sx={{ marginLeft: 'auto' }}>
          <Button
            variant="contained"
            color="primary"
            onClick={handleCrearUsuario}
          >
            Crear Usuario
          </Button>
        </Grid>
      </Grid>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 500 }} aria-label="custom pagination table">
          <TableHead>
            <TableRow>
              <TableCell>Nombre</TableCell>
              <TableCell align="right">Apellido</TableCell>
              <TableCell align="right">Usuario</TableCell>
              <TableCell align="center">Inactivo / Activo</TableCell>
              <TableCell align="right">Fecha de Creación</TableCell>
              <TableCell align="center">Acciones</TableCell>
            </TableRow>
          </TableHead>

          <TableBody>
            {(rowsPerPage > 0
              ? usuarios.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              : usuarios
            ).map((usuario) => (
              <TableRow key={usuario.id}>
                <TableCell component="th" scope="row">
                  {usuario.u_nombre}
                </TableCell>
                <TableCell style={{ width: 160 }} align="right">
                  {usuario.u_apellido}
                </TableCell>
                <TableCell style={{ width: 160 }} align="right">
                  {usuario.u_usuario}
                </TableCell>
                <TableCell style={{ width: 160 }} align="center">
                  {/* Switch for active/inactive state */}
                  <Switch
                    checked={usuario.u_estado === 1}
                    onChange={() => handleToggleEstado(usuario.id, usuario.u_estado)}
                    color="primary"
                  />
                </TableCell>
                <TableCell style={{ width: 160 }} align="right">
                  {new Date(usuario.u_fechacreacion).toLocaleDateString()}
                </TableCell>
                <TableCell align="center">
                  <IconButton onClick={() => handleActualizarUsuario(usuario.id)} color="primary" aria-label="editar">
                    <EditIcon />
                  </IconButton>
                  <IconButton onClick={() => handleEliminarUsuario(usuario.id)} color="secondary" aria-label="eliminar">
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            {emptyRows > 0 && (
              <TableRow style={{ height: 53 * emptyRows }}>
                <TableCell colSpan={6} />
              </TableRow>
            )}
          </TableBody>

          <TableFooter>
            <TableRow>
              <TablePagination
                rowsPerPageOptions={[5, 10, 25, { label: 'Todos', value: -1 }]}
                colSpan={3}
                count={usuarios.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handlePageChange}
                onRowsPerPageChange={handleRowsPerPageChange}
                ActionsComponent={TablePaginationActions}
              />
            </TableRow>
          </TableFooter>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default Usuario;
