import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel, FormControl, Table, TableBody, TableCell, TableContainer, TableRow, Paper, TableHead } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Import the alert component
import { isAuthenticated } from '../../utils/auth'; // Import the isAuthenticated function

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null); // State for alerts
  const [nombreProyecto, setNombreProyecto] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [hitos, setHitos] = useState([]); // Milestones
  const [resultadoEsperado, setResultadoEsperado] = useState('');
  const [pruebaRealizada, setPruebaRealizada] = useState('');
  const [estadoPrueba, setEstadoPrueba] = useState('');
  const [showPlanDePrueba, setShowPlanDePrueba] = useState(false); // State for showing test plan fields
  const [hitoSeleccionado, setHitoSeleccionado] = useState(''); // Selected milestone
  const navigate = useNavigate();
  const { id } = useParams(); // Get project ID from URL

  // Check if the user is authenticated when the component loads
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirect to login if not authenticated
    }
  }, [navigate]);

  // Fetch project data
  useEffect(() => {
    const fetchProyecto = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/proyectos/proyecto/${id}`);
        const proyecto = response.data;

        setNombreProyecto(proyecto.nombre);
        setDescripcion(proyecto.descripcion);
        setHitos(proyecto.hitos); // Assuming the project has a 'hitos' field
      } catch (error) {
        console.error('Error fetching project data:', error);
        setShowAlert({
          severity: 'error',
          message: 'There was an error loading the project data.',
        });
      }
    };

    if (id) {
      fetchProyecto();
    }
  }, [id]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!resultadoEsperado || !pruebaRealizada || !estadoPrueba || !hitoSeleccionado) {
      setShowAlert({
        severity: 'error',
        message: 'All fields for the test plan are required.',
      });
      return;
    }

    const planDePrueba = {
      resultadoEsperado,
      pruebaRealizada,
      estadoPrueba,
      hito: hitoSeleccionado,
    };

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'Authentication token not found.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      // Submit the test plan
      const response = await axios.post(`http://localhost:5000/api/proyectos/crearPlanDePrueba/${id}`, planDePrueba, config);

      if (response.data.msg === 'Test plan created successfully') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg,
        });

        // Redirect after 2 seconds
        setTimeout(() => {
          navigate('/proyectos/tareas');
        }, 2000);
      }
    } catch (error) {
      console.error('Error creating test plan:', error.response || error);
      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'There was an error creating the test plan. Please try again.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom align="center">
        Crear Plan de Prueba
      </Typography>

      {/* Project Data */}
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Nombre del Proyecto"
            value={nombreProyecto}
            disabled
            variant="outlined"
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Descripción"
            value={descripcion}
            disabled
            variant="outlined"
          />
        </Grid>
      </Grid>

      {/* Button to show test plan fields */}
      <Grid item xs={12} mt={2}>
        <Button 
          variant="contained" 
          color="primary" 
          fullWidth 
          onClick={() => setShowPlanDePrueba(true)}
        >
          Crear Plan de Prueba
        </Button>
      </Grid>

      {/* Display the test plan fields only if the button is clicked */}
      {showPlanDePrueba && (
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2} mt={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Resultado Esperado"
                value={resultadoEsperado}
                onChange={(e) => setResultadoEsperado(e.target.value)}
                variant="outlined"
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Prueba Realizada"
                value={pruebaRealizada}
                onChange={(e) => setPruebaRealizada(e.target.value)}
                variant="outlined"
              />
            </Grid>

            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required variant="outlined">
                <InputLabel>Estado de la Prueba</InputLabel>
                <Select
                  value={estadoPrueba}
                  onChange={(e) => setEstadoPrueba(e.target.value)}
                  label="Estado de la Prueba"
                >
                  <MenuItem value="Pendiente">Pendiente</MenuItem>
                  <MenuItem value="Aprobada">Aprobada</MenuItem>
                  <MenuItem value="Rechazada">Rechazada</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            {/* Display the milestones in a table */}
            <Grid item xs={12}>
              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Hito</TableCell>
                      <TableCell>Acción</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {hitos.map((hito, index) => (
                      <TableRow key={index}>
                        <TableCell>{hito.nombre}</TableCell>
                        <TableCell>
                          <Button
                            variant="outlined"
                            onClick={() => setHitoSeleccionado(hito.nombre)}
                            color={hitoSeleccionado === hito.nombre ? 'primary' : 'default'}
                          >
                            Seleccionar
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Grid>

            <Grid item xs={12} mt={2}>
              <Button type="submit" variant="contained" color="primary" fullWidth>
                Crear Plan de Prueba
              </Button>
            </Grid>
          </Grid>
        </form>
      )}
    </Box>
  );
};

export default CrearPlanDePrueba;
