import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box } from '@mui/material';
import { styled } from '@mui/system';
import logo from '../assets/LogoPrincipal.png'; // Asegúrate de que la ruta sea correcta
import { useNavigate } from 'react-router-dom';

// Estilo para el logo circular
const CircularLogo = styled('img')({
    width: '350px', // Ajusta el tamaño según sea necesario
    height: '200px',
    borderRadius: '50%', // Esto hace que el logo sea circular
    display: 'block',
    margin: '0 auto', // Centra el logo
});

const Login = () => {
    const [usuario, setUsuario] = useState('');
    const [password, setPassword] = useState('');
    const [errors, setErrors] = useState({});
    const navigate = useNavigate(); // Hook para navegación

    const handleSubmit = async (event) => {
      event.preventDefault();
  
      // Validación básica
      const newErrors = {};
      if (!usuario) newErrors.usuario = 'El usuario es requerido';
      if (!password) newErrors.password = 'La contraseña es requerida';
  
      if (Object.keys(newErrors).length > 0) {
          setErrors(newErrors);
          return; // Detenemos el envío del formulario si hay errores
      }
  
      try {
          const response = await fetch('http://localhost:5000/api/auth/login', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify({ username: usuario, password }),
          });
  
          // Si la respuesta no es ok, intenta obtener más información
          if (!response.ok) {
              const errorData = await response.json();

              throw new Error(errorData.msg );
          }
  
          const data = await response.json();
          navigate('/home'); // Redirigir si el inicio de sesión fue exitoso
  
      } catch (error) {
          setErrors({ submit: error.ms });
      }
  };

    return (
        <Container maxWidth="xs" sx={{ mt: 8 }}>
            <CircularLogo src={logo} alt="Logo" />
            <Typography variant="h4" align="center" gutterBottom>
                Iniciar Sesión
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
                <TextField
                    required
                    fullWidth
                    label="Usuario" // "Usuario"
                    margin="normal"
                    value={usuario}
                    onChange={(e) => {
                        setUsuario(e.target.value);
                        if (errors.usuario) {
                            setErrors({ ...errors, usuario: undefined }); // Limpiar error si hay datos
                        }
                    }}
                    error={Boolean(errors.usuario)}
                    helperText={errors.usuario}
                />
                <TextField
                    required
                    fullWidth
                    label="Contraseña" // "Contraseña"
                    type="password"
                    margin="normal"
                    value={password}
                    onChange={(e) => {
                        setPassword(e.target.value);
                        if (errors.password) {
                            setErrors({ ...errors, password: undefined }); // Limpiar error si hay datos
                        }
                    }}
                    error={Boolean(errors.password)}
                    helperText={errors.password}
                />
                {errors.submit && <Typography color="error">{errors.submit}</Typography>} {/* Mensaje de error general */}
                <Button type="submit" variant="contained" color="primary" fullWidth>
                    Iniciar Sesión
                </Button>
            </Box>
        </Container>
    );
};

export default Login;
